CDECK  ID>, IPHO_ISMAPPED
      INTEGER FUNCTION IPHO_ISMAPPED(ipdg)
c*********************************************************************
c
c     Returns PDG ID of particle to which ipdg is mapped. If it is not
c     mapped to anything the original ipdg is returned 
c
c     input:   PDG ID of incoming particle
c
c     output:  PDG ID under which the particle is tracked in 
c              initialization
c
c*********************************************************************
      IMPLICIT NONE
      SAVE

      INTEGER ipdg, i

      INTEGER IDXMPAR, MPARMAX, MPARCONF, NMPAR, IKNOWN
      INTEGER MPMAPP, MAXMAPPS, NMAPP, MPAR, IPAVAIL
      DOUBLE PRECISION SQSGLOBMAX
      PARAMETER (MPARMAX=40)
      PARAMETER (MAXMAPPS=40)
      PARAMETER (IKNOWN=9)
      DIMENSION IPAVAIL(IKNOWN)
      DATA IPAVAIL /2212, 211, 111, 22, 2112, 3122, 3112, 321, 311/
      COMMON /POBEAM/ MPARCONF(2, MPARMAX), MPMAPP(2,MAXMAPPS),
     &           MPAR(2), SQSGLOBMAX, NMPAR, IDXMPAR, NMAPP

      
      IPHO_ISMAPPED = ipdg
      If (nmapp.lt.1) Return
      Do i=1,nmapp
        If (ipdg==mpmapp(1,i)) Then
          IPHO_ISMAPPED = mpmapp(2,i)
          Exit
        End If
c   The array should not contain any zeros due in between values
        If (mpmapp(1,i) == 0) Exit
      End Do

      End 


 
 


