C  obsolete cut-off information
      DOUBLE PRECISION PTCUT,PTANO,FPS,FPH,PSOMIN,XSOMIN
      COMMON /POCUT1/ PTCUT(4),PTANO(4),FPS(4),FPH(4),PSOMIN,XSOMIN
      
      DIMENSION PVIRT(2),SIGSRH(2),FSUT(2),FSUL(2),ILPAP(2),IHEL(2)

      DIMENSION PD(-6:6),FH_T(2),FH_L(2) 
      DOUBLE PRECISION PHO_PTCUT
      EXTERNAL         PHO_PTCUT
      DATA IPCOMB / 0 /

C  debug
      IF (LPRI.GT.4 .AND. IDEB(15).GE.10)
     &WRITE(LO,'(1X,A,/10X,I3,2I6,1P3E12.4)')
     &  'PHO_CSINT: called with IP, IFP1, IFP2, ECM, PVIR1, PVIR2',
     &  IP,IFPA,IFPB,ECM,PVIR2A,PVIR2B
C**anfe Initialize the ptcut common block information
      ptcut(ip) = PHO_PTCUT(ecm, ip)
C  check currently stored cross sections
      IF((IP.EQ.IPFIL).AND.(ECM.EQ.ECMFIL)
     &   .AND.(PVIR2A.EQ.P2AFIL).AND.(PVIR2B.EQ.P2BFIL)
     &   .AND.(IFPA.EQ.IFAFIL).AND.(IFPB.EQ.IFBFIL).AND.
     &   (IPCOMB==IDXMPAR)) THEN
C  nothing to calculate
        IF(IDEB(15).GE.20 .AND. LPRI.GT.4)
     &    WRITE(LO,'(1X,A)') 'PHO_CSINT: nothing done'
        RETURN
      ELSE

C  copy to local fields
        ILPAP(1) = IFPA
        ILPAP(2) = IFPB
        IHEL(1)  = IHLA
        IHEL(2)  = IHLB
        PVIRT(1) = PVIR2A
        PVIRT(2) = PVIR2B
C  load cross sections from interpolation table
        IF(ECM.LE.SIGECM(1,IP,IDXMPAR)) THEN
          I1 = 1
          I2 = 2
        ELSE IF(ECM.LE.SIGECM(ISIMAX(IDXMPAR),IP,IDXMPAR)) THEN
          DO 50 I=2,ISIMAX(IDXMPAR)
            IF(ECM.LE.SIGECM(I,IP,IDXMPAR)) GOTO 200
 50       CONTINUE
 200      CONTINUE
          I1 = I-1
          I2 = I
        ELSE
          IF (LPRI.GT.4)
     &    WRITE(LO,'(/1X,A,2E12.3)')
     &      'PHO_CSINT: too high energy',ECM,
     &      SIGECM(ISIMAX(IDXMPAR),IP,IDXMPAR)
          CALL PHO_PREVNT(-1)
          I1 = ISIMAX(IDXMPAR)-1
          I2 = ISIMAX(IDXMPAR)
        ENDIF
        FAC2=0.D0
        IF(I1.NE.I2) FAC2=LOG(ECM/SIGECM(I1,IP,IDXMPAR))
     &                    /LOG(SIGECM(I2,IP,IDXMPAR)/
     &                    SIGECM(I1,IP,IDXMPAR))
        FAC1=1.D0-FAC2

C  cross section dependence on photon virtualities
        DO 140 K=1,2
          FSUP(K) = 1.D0
          FSUD(K) = 1.D0
          FSUH(K) = 1.D0
          IF(ILPAP(K).EQ.22) THEN
            IF(ISWMDL(10).GE.1) THEN
              FSUP(K) = 0.D0
              FSUT(K) = 0.D0
              FSUL(K) = 0.D0
              FSUH(K) = 0.D0
C  GVDM factors for transverse/longitudinal photons
              DO 150 I=1,3
                FSUT(K) = FSUT(K)+PARMDL(26+I)
     &                   /(1.D0+PVIRT(K)/PARMDL(30+I))**2
                FSUL(K) = FSUL(K)
     &                   +PARMDL(26+I)*PVIRT(K)/(4.D0*PARMDL(30+I))
     &                   /(1.D0+PVIRT(K)/PARMDL(30+I))**2
 150          CONTINUE
              FSUT(K) = FSUT(K)+PARMDL(30)/(1.D0+PVIRT(K)/PARMDL(34))
C  transverse part
              IF((ABS(IHEL(K)).EQ.1).OR.(ISWMDL(10).EQ.1)) THEN
                FSUP(K) = FSUT(K)
                FSUH(K) = FSUT(K)/(FSUT(K)+FSUL(K))
C  diffraction of trans. photons corresponds mainly to leading twist
                FSUD(K) = 1.D0
              ENDIF
C  longitudinal (scalar) part
              IF((IHEL(K).LE.0).OR.(ISWMDL(10).EQ.1)) THEN
                FSUP(K) = FSUP(K)+FSUL(K)
                FSUH(K) = FSUH(K)+FSUL(K)/(FSUT(K)+FSUL(K))
C  diffraction of long. photons corresponds mainly to higher twist
                FSUD(K) = 0.5D0*LOG(((ECM*PARMDL(45))**2+PVIRT(K))
     &                   /((0.765D0+PARMDL(46))**2+PVIRT(K)))
     &                   /LOG(ECM*PARMDL(45)/(0.765D0+PARMDL(46)))
              ENDIF
C  debug output
              if(ideb(15).ge.10) then
                IF (LPRI.GT.4)
     &          WRITE(LO,'(1x,2a,2i3,/,5x,1p5e12.4)') 'PHO_CSINT: ',
     &            'side,helicity,F_tran,F_long,F_eff,F_hard,F_diff',
     &            K,IHEL(K),FSUT(K),FSUL(K),FSUP(K),FSUH(K),FSUD(K)
              endif
            ENDIF
          ENDIF
 140    CONTINUE

        FACP = FSUP(1)*FSUP(2)
        FACH = FSUH(1)*FSUH(2)
        FACD = FSUD(1)*FSUD(2)

C  matching of model cross section to F2(x,Q2,P2) in limit of Q2 >> P2

        if((ILPAP(1).eq.22).and.(ILPAP(2).eq.22)
     &     .and.(IPAMDL(117).gt.0)) then
C  check kinematic limit
          Q2_max = max(PVIRT(1),PVIRT(2))
          Q2_min = min(PVIRT(1),PVIRT(2))
          if((Q2_max.gt.1.D0).and.(Q2_min.lt.1.D0)) then

C  calculate F2 from current parton density
            if(PVIRT(1).gt.PVIRT(2)) then
              K = 2
            else
              K = 1
            endif
            Q2 = Q2_max
            P2 = Q2_min
            X = Q2/(ECM**2+Q2+P2)
            CALL PHO_ACTPDF(ILPAP(K),K)
            CALL PHO_PDF(K,X,Q2,P2,PD)
C  light quark contribution
            F2_light = 0.D0
            do j=1,3
              F2_light = F2_light+Q_ch2(j)*(PD(j)+PD(-j))
            enddo
C  heavy quark contribution
            CALL PHO_QPMPDF(4,X,Q2,0.D0,P2,xpdf_c)
            F2_c = 2.D0*4.D0/9.D0*xpdf_c
            F2 = (F2_light+F2_c)

C  calculate model prediction
            SIGTOT = FAC2*SIGTAB(1,I2,IP,IDXMPAR)+
     &               FAC1*SIGTAB(1,I1,IP,IDXMPAR)
            SIGINE = FAC2*SIGTAB(28,I2,IP,IDXMPAR)+
     &               FAC1*SIGTAB(28,I1,IP,IDXMPAR)
            CALL PHO_HARINT(IP,ECM,0.D0,0.D0,0,Max_pro_2,3,4,1)

            if(ISWMDL(10).ge.2) then

C  calculate all helicity combinations
              if(IPAMDL(115).eq.0) then
                SIGDIH    = HSig(14,IDXMPAR)
                SIGSRH(1) = HSig(10,IDXMPAR)+HSig(11,IDXMPAR)
                SIGSRH(2) = HSig(12,IDXMPAR)+HSig(13,IDXMPAR)
                SIGtmp = SIGTOT-SIGDIH-SIGSRH(1)-SIGSRH(2)
C  photon helicity factors
                FH_T(1) = FSUT(1)/(FSUT(1)+FSUL(1))
                FH_L(1) = 1.D0-FH_T(1)
                FH_T(2) = FSUT(2)/(FSUT(2)+FSUL(2))
                FH_L(2) = 1.D0-FH_T(2)
                SIG_TT = SIGtmp*FSUT(1)*FSUT(2)
     &                  + SIGDIH*FH_T(1)*FH_T(2)
     &                  + SIGSRH(1)*FH_T(1)*FSUT(2)
     &                  + SIGSRH(2)*FSUT(1)*FH_T(2)
                SIG_TL = SIGtmp*FSUT(1)*FSUL(2)
     &                  + SIGDIH*FH_T(1)*FH_L(2)
     &                  + SIGSRH(1)*FH_T(1)*FSUL(2)
     &                  + SIGSRH(2)*FSUT(1)*FH_L(2)
                SIG_LT = SIGtmp*FSUL(1)*FSUT(2)
     &                  + SIGDIH*FH_L(1)*FH_T(2)
     &                  + SIGSRH(1)*FH_L(1)*FSUT(2)
     &                  + SIGSRH(2)*FSUL(1)*FH_T(2)
                SIG_LL = SIGtmp*FSUL(1)*FSUL(2)
     &                  + SIGDIH*FH_L(1)*FH_L(2)
     &                  + SIGSRH(1)*FH_L(1)*FSUL(2)
     &                  + SIGSRH(2)*FSUL(1)*FH_L(2)
              else
C  use explicit PDF virtuality dependence (pre-tabulated)
                SIGDIH    = HSig(14,IDXMPAR)
                SIGSRH(1) = HSig(10,IDXMPAR)+HSig(11,IDXMPAR)
                SIGSRH(2) = HSig(12,IDXMPAR)+HSig(13,IDXMPAR)
                SIGtmp = SIGTOT-SIGDIH-SIGSRH(1)-SIGSRH(2)
                IF (LPRI.GT.4)
     &          WRITE(LO,*)' PHO_CSINT: invalid option for F2 matching'
                stop
*               CALL PHO_HARINT(IP,ECM,PVIRT(1),PVIRT(2),0,
*    &                          Max_pro_2,3,4,1)
*               SIG_TT = SIGtmp*FSUT(1)*FSUT(2)
*    &            + HSig(10,IDXMPAR)+HSig(12,IDXMPAR)+
*    &            +HSig(14,IDXMPAR)+HSig(16,IDXMPAR)+HSig(18,IDXMPAR)
*               SIG_TL = SIGtmp*FSUT(1)*FSUL(2)
*    &            + HSig(10,IDXMPAR)+HSig(12,IDXMPAR)+
*    &            + HSig(14,IDXMPAR)+HSig(16,IDXMPAR)+HSig(19,IDXMPAR)
*               SIG_LT = SIGtmp*FSUL(1)*FSUT(2)
*    &            + HSig(11,IDXMPAR)+HSig(13,IDXMPAR)+
*    &            + HSig(15,IDXMPAR)+HSig(17,IDXMPAR)+HSig(20,IDXMPAR)
*               SIG_LL = SIGtmp*FSUL(1)*FSUL(2)
*    &            + HSig(11,IDXMPAR)+HSig(13,IDXMPAR)+
*    &            + HSig(15,IDXMPAR)+HSig(17,IDXMPAR)+HSig(21,IDXMPAR)
              endif
              Xnu = Ecm*Ecm+Q2+P2
              F2_fac = Q2*Xnu/sqrt(Xnu*Xnu-Q2*P2)/(4.D0*Pi*Pi)
     &             *137.D0/GeV2mb
              if(K.eq.2) then
                F2m = F2_fac*(SIG_TT+SIG_LT-0.5D0*SIG_TL-0.5D0*SIG_LL)
                F2s = F2_fac*SIGtmp*(FSUT(1)*FSUT(2)+FSUL(1)*FSUT(2)
     &               -0.5D0*FSUT(1)*FSUL(2)-0.5D0*FSUL(1)*FSUL(2))
              else
                F2m = F2_fac*(SIG_TT+SIG_TL-0.5D0*SIG_LT-0.5D0*SIG_LL)
                F2s = F2_fac*SIGtmp*(FSUT(1)*FSUT(2)+FSUT(1)*FSUL(2)
     &               -0.5D0*FSUL(1)*FSUT(2)-0.5D0*FSUL(1)*FSUL(2))
              endif

            else

C  assume sig_eff = sigtot
              SIGDIH    = HSig(14,IDXMPAR)
              SIGSRH(1) = HSig(10,IDXMPAR)+HSig(11,IDXMPAR)
              SIGSRH(2) = HSig(12,IDXMPAR)+HSig(13,IDXMPAR)
              SIGtmp = SIGTOT-SIGSRH(1)-SIGSRH(2)-SIGDIH
              SIGeff = SIGtmp*FSUP(1)*FSUP(2)
     &                +SIGSRH(1)*FSUP(2)+SIGSRH(2)*FSUP(1)+SIGDIH
              Xnu = Ecm*Ecm+Q2+P2
              F2_fac = Q2*Xnu/sqrt(Xnu*Xnu-Q2*P2)/(4.D0*Pi*Pi)
     &             *137.D0/GeV2mb
              F2m = F2_fac*SIGeff
              F2s = F2_fac*SIGtmp*FSUP(1)*FSUP(2)
            endif
*           WRITE(LO,*)' PHO_CSINT: Q2_1,Q2_2,W ',PVIRT(1),PVIRT(2),Ecm
*           WRITE(LO,*)' PHO_CSINT: F2_mod,F2_pdf,mod/pdf ',
*    &                    F2m,F2,F2m/F2

C  global factor to re-scale suppression of soft contributions
            Fcorr = (F2-F2m+F2s)/F2s
*          WRITE(LO,*)' PHO_CSINT: re-scaling factor: ',Fcorr,FACP*Fcorr
            FACP = FACP*Fcorr

          endif
        endif

        SIGTOT = (FAC2*SIGTAB(1,I2,IP,IDXMPAR)+
     &            FAC1*SIGTAB(1,I1,IP,IDXMPAR))*FACP
        SIGINE = (FAC2*SIGTAB(28,I2,IP,IDXMPAR)+
     &            FAC1*SIGTAB(28,I1,IP,IDXMPAR))*FACP
        SIGELA = (FAC2*SIGTAB(2,I2,IP,IDXMPAR)+
     &            FAC1*SIGTAB(2,I1,IP,IDXMPAR))*FACP
        J = 2
        DO 5 I=0,4
          DO 6 K=0,4
            J = J+1
            SIGVM(I,K) = (FAC2*SIGTAB(J,I2,IP,IDXMPAR)+
     &                    FAC1*SIGTAB(J,I1,IP,IDXMPAR))*FACP**2
 6        CONTINUE
 5      CONTINUE

        SIGDIR = FAC2*SIGTAB(29,I2,IP,IDXMPAR)+
     &           FAC1*SIGTAB(29,I1,IP,IDXMPAR)
        SIGHAR = FAC2*SIGTAB(58,I2,IP,IDXMPAR)+
     &           FAC1*SIGTAB(58,I1,IP,IDXMPAR)
C  suppression of multi-pomeron graphs (diffraction)
        SIGLSD(1) = (FAC2*SIGTAB(30,I2,IP,IDXMPAR)+
     &               FAC1*SIGTAB(30,I1,IP,IDXMPAR))*FACP*FSUP(2)*FSUD(1)
        SIGLSD(2) = (FAC2*SIGTAB(31,I2,IP,IDXMPAR)+
     &               FAC1*SIGTAB(31,I1,IP,IDXMPAR))*FACP*FSUP(1)*FSUD(2)
        SIGHSD(1) = (FAC2*SIGTAB(32,I2,IP,IDXMPAR)+
     &               FAC1*SIGTAB(32,I1,IP,IDXMPAR))*FACP*FSUP(2)*FSUD(1)
        SIGHSD(2) = (FAC2*SIGTAB(33,I2,IP,IDXMPAR)+
     &               FAC1*SIGTAB(33,I1,IP,IDXMPAR))*FACP*FSUP(1)*FSUD(2)
        SIGLDD    = (FAC2*SIGTAB(34,I2,IP,IDXMPAR)+
     &               FAC1*SIGTAB(34,I1,IP,IDXMPAR))*FACP**2*FACD
        SIGHDD    = (FAC2*SIGTAB(35,I2,IP,IDXMPAR)+
     &               FAC1*SIGTAB(35,I1,IP,IDXMPAR))*FACP
        SIGCDF(0) = (FAC2*SIGTAB(36,I2,IP,IDXMPAR)+
     &               FAC1*SIGTAB(36,I1,IP,IDXMPAR))*FACP**2
        SIGTR1(1) = (FAC2*SIGTAB(60,I2,IP,IDXMPAR)+
     &               FAC1*SIGTAB(60,I1,IP,IDXMPAR))*FACP*FSUP(2)*FSUD(1)
        SIGTR1(2) = (FAC2*SIGTAB(61,I2,IP,IDXMPAR)+
     &               FAC1*SIGTAB(61,I1,IP,IDXMPAR))*FACP*FSUP(2)*FSUD(1)
        SIGTR2(1) = (FAC2*SIGTAB(62,I2,IP,IDXMPAR)+
     &               FAC1*SIGTAB(62,I1,IP,IDXMPAR))*FACP*FSUP(1)*FSUD(2)
        SIGTR2(2) = (FAC2*SIGTAB(63,I2,IP,IDXMPAR)+
     &               FAC1*SIGTAB(63,I1,IP,IDXMPAR))*FACP*FSUP(1)*FSUD(2)
        SIGLOO    = (FAC2*SIGTAB(64,I2,IP,IDXMPAR)+
     &               FAC1*SIGTAB(64,I1,IP,IDXMPAR))*FACP
        SIGDPO(1) = (FAC2*SIGTAB(65,I2,IP,IDXMPAR)+
     &               FAC1*SIGTAB(65,I1,IP,IDXMPAR))*FACP**2
        SIGDPO(2) = (FAC2*SIGTAB(66,I2,IP,IDXMPAR)+
     &               FAC1*SIGTAB(66,I1,IP,IDXMPAR))*FACP**2
        SIGDPO(3) = (FAC2*SIGTAB(67,I2,IP,IDXMPAR)+
     &               FAC1*SIGTAB(67,I1,IP,IDXMPAR))*FACP**2
        SIGDPO(4) = (FAC2*SIGTAB(68,I2,IP,IDXMPAR)+
     &               FAC1*SIGTAB(68,I1,IP,IDXMPAR))*FACP**2

C  corrections due to photon virtuality dependence of PDFs
        if(iswmdl(2).eq.1) then
          CALL PHO_HARINT(IP,ECM,0.D0,0.D0,0,Max_pro_2,3,4,1)
C  minimum bias event generation
          IF(IPAMDL(115).GE.1) THEN
C  all the virtuality dependence is given by PDF parametrization
            SIGHIN = FAC2*SIGTAB(80,I2,IP,IDXMPAR)+
     &               FAC1*SIGTAB(80,I1,IP,IDXMPAR)
            IF(IPAMDL(116).GE.2) THEN
C  direct interaction according to full QPM calculation
              SIGDIH = HSig(14,IDXMPAR)
              SIGSRH(1) = HSig(10,IDXMPAR)+HSig(11,IDXMPAR)
              SIGSRH(2) = HSig(12,IDXMPAR)+HSig(13,IDXMPAR)
            ELSE
C  direct interaction suppressed according to helicity factor
              SIGDIH = HSig(14,IDXMPAR)*FACH
              SIGSRH(1) = (HSig(10,IDXMPAR)+HSig(11,IDXMPAR))*FSUH(1)
              SIGSRH(2) = (HSig(12,IDXMPAR)+HSig(13,IDXMPAR))*FSUH(2)
            ENDIF
            IF (LPRI.GT.4)
     &      WRITE(LO,*)' PHO_CSINT: option not supported yet'
            stop
          ELSE
C  rescale relevant hard processes
            SIGDIH    = HSig(14,IDXMPAR)
            SIGSRH(1) = HSig(10,IDXMPAR)+HSig(11,IDXMPAR)
            SIGSRH(2) = HSig(12,IDXMPAR)+HSig(13,IDXMPAR)
            SIGtmp = SIGINE-(SIGDIH+SIGSRH(1)+SIGSRH(2))*FACP
            SIGDIR = HSig(14,IDXMPAR)*FACH+SIGSRH(1)*FSUH(1)*FSUP(2)
     &              +SIGSRH(2)*FSUP(1)*FSUH(2)
            SIGINE = SIGtmp+SIGDIR
            SIGTOT = SIGINE+SIGELA
          ENDIF
        else
C  only hard interactions
          CALL PHO_HARINT(IP,ECM,0.D0,0.D0,0,Max_pro_2,3,4,1)
          SIGSRH(1) = (HSig(10,IDXMPAR)+HSig(11,IDXMPAR))*FSUH(1)
          SIGSRH(2) = (HSig(12,IDXMPAR)+HSig(13,IDXMPAR))*FSUH(2)
          SIGDIR = HSig(14,IDXMPAR)+SIGSRH(1)+SIGSRH(2)
          SIGHAR = HSig(9,IDXMPAR)*FACH
        endif

        SIG1SO = (FAC2*SIGTAB(37,I2,IP,IDXMPAR)+
     &            FAC1*SIGTAB(37,I1,IP,IDXMPAR))*FACP
        SIG1HA = (FAC2*SIGTAB(38,I2,IP,IDXMPAR)+
     &            FAC1*SIGTAB(38,I1,IP,IDXMPAR))*FACH
        SLOEL = FAC2*SIGTAB(39,I2,IP,IDXMPAR)+
     &          FAC1*SIGTAB(39,I1,IP,IDXMPAR)
        J = 39
        DO 9 I=1,4
          DO 10 K=1,4
            J = J+1
            SLOVM(I,K) = FAC2*SIGTAB(J,I2,IP,IDXMPAR)+
     &                   FAC1*SIGTAB(J,I1,IP,IDXMPAR)
 10       CONTINUE
 9      CONTINUE
        SIGPOM = (FAC2*SIGTAB(56,I2,IP,IDXMPAR)+
     &            FAC1*SIGTAB(56,I1,IP,IDXMPAR))*FACP
        SIGREG = (FAC2*SIGTAB(57,I2,IP,IDXMPAR)+
     &            FAC1*SIGTAB(57,I1,IP,IDXMPAR))*FACP

        IPFIL  = IP
        IFAFIL = IFPA
        IFBFIL = IFPB
        ECMFIL = ECM
        ipcomb = idxmpar
        P2AFIL = PVIR2A
        P2BFIL = PVIR2B

        IF(IDEB(15).GE.20 .AND. LPRI.GT.4)
     &    WRITE(LO,'(1X,A)') 'PHO_CSINT: cross sections calculated'

      ENDIF

      END



      DOUBLE PRECISION FUNCTION PHO_CT14ALPHAS (QQ)

      IMPLICIT DOUBLE PRECISION (A-H,O-Z)

      PARAMETER (MXX = 201, MXQ = 40, MXF = 6, MaxVal=4)
      PARAMETER (MXPQX = (MXF+1+MaxVal) * MXQ * MXX)
      DOUBLE PRECISION Alsout
      
      COMMON
     > / CtqPar1 / qBase,XV(0:MXX), TV(0:MXQ), UPD(MXPQX),AlsCTEQ(0:mxq)
     > / CtqPar2 / Nx, Nt, NfMx, MxVal
     > / XQrange / Qini, Qmax, Xmin
     > /Setchange/ Isetch, ipdsset, ipdsformat

      DATA Q, JQ /-1D0, 0/
      SAVE

      if (ipdsset.ne.1) 
     >  STOP 'PHO_CT14ALPHAS: the PDF table was not initialized'

      
      if (ipdsformat.lt.11) then
        IF (LPRI.GT.4)
     &   WRITE(LO,*)  
     >  'STOP in PHO_CT14ALPHAS: the PDF table file has an older format'
        IF (LPRI.GT.4)
     &   WRITE(LO,*) 
     >    'and does not include the table of QCD coupling values.'
        IF (LPRI.GT.4)
     &   WRITE(LO,*) 
     >    'You can still compute the PDFs, but do not call'
        IF (LPRI.GT.4)
     &   WRITE(LO,*) 
     >   'the pho_CT14alphas function for the interpolation of alpha_s.'
        stop
      endif

      Q = QQ
      tt = log(log(Q/qBase))

c         --------------   Find lower end of interval containing Q, i.e.,
c                          get jq such that qv(jq) .le. q .le. qv(jq+1)...
      JLq = -1
      JU = NT+1
 13   If (JU-JLq .GT. 1) Then
        JM = (JU+JLq) / 2
        If (tt .GE. TV(JM)) Then
            JLq = JM
          Else
            JU = JM
          Endif
          Goto 13
       Endif

      If     (JLq .LE. 0) Then
         Jq = 0
      Elseif (JLq .LE. Nt-2) Then
C                                  keep q in the middle, as shown above
         Jq = JLq - 1
      Else
C                         JLq .GE. Nt-1 case:  Keep at least 4 points >= Jq.
        Jq = Nt - 3

      Endif
C                                 This is the interpolation variable in Q
      CALL PHO_POlINT4F (TV(jq), AlsCTEQ(jq), tt, Alsout)
      
      PHO_CT14ALPHAS = Alsout
      
      Return
      End



      SUBROUTINE PHO_CT14GETPARS(xmin,Qini,Qmax,Nloops,Nfl)
c**********************************************************************
c Get various parameters associated with the PDF grid
c Output: xmin  is the minimal value of x 
c         Qmin  is the initial Q scale  
c         Qmax  is the maximal Q scale
c         Nloop is the number of QCD loops
c         Nfl   is the maximal number of quark flavors
c**********************************************************************
      IMPLICIT NONE
      SAVE
      DOUBLE PRECISION QINI0, Qmax0, Xmin0, xmin, Qini, Qmax
      INTEGER Nloops, Ipk, Iorder, Nfl, Nfl0
      DOUBLE PRECISION AlfaQ, Qalfa

      COMMON / XQrange / Qini0, Qmax0, Xmin0
      COMMON / QCDtbl /  AlfaQ, Qalfa, Ipk, Iorder, Nfl0
     
      Qini=Qini0; Qmax=Qmax0; Xmin=Xmin0
      Nloops=Iorder-1; Nfl=Nfl0
      
      return 
      end



      DOUBLE PRECISION FUNCTION PHO_CT14MASS(i)
c**********************************************************************
c     Returns the value of the quark mass for the i-th flavor 
c     The flavors are:
c     1  2  3  4  5  6
c     u  d  s  c  b  t
c**********************************************************************
      IMPLICIT NONE
      SAVE
