     &     0,     0,     0,     0,     0,     0,     0,     0,     0/

      ID_pdg_max = i_tab_max

C  copy from local to global variables
      do i=1,i_tab_max
        ID_pdg_list(i) = number(i)
        name_list(i)   = name(i)
        xm_list(i)     = xmass(i)
        gam_list(i)    = gamma(i)
        ich3_list(i)   = ich3(i)
        iba3_list(i)   = iba3(i)
        do j=1,3
          iq_list(j,i)   = iq_linear(3*(i-1)+j)
          idec_list(j,i) = idec_linear(3*(i-1)+j)
        enddo
      enddo

C  initialize hash table
      CALL PHO_CPCINI(ID_pdg_max,ID_pdg_list,ID_list)

      itmp = IDEB(71)
      IDEB(71) = -1

C  quark index table for mesons
      do i=1,6
        do j=1,6
          id_psm_list(i,j) = IPHO_PDG2ID(id_psm_linear(6*(j-1)+i))
          id_vem_list(i,j) = IPHO_PDG2ID(id_vem_linear(6*(j-1)+i))
        enddo
      enddo

C  quark index table for baryons
      do i=1,6
        do j=1,6
          do k=1,6
            id_b8_list(i,j,k)  =
     &        IPHO_PDG2ID(id_b8_linear(36*(k-1)+6*(j-1)+i))
            id_b10_list(i,j,k) =
     &        IPHO_PDG2ID(id_b10_linear(36*(k-1)+6*(j-1)+i))
          enddo
        enddo
      enddo

      IDEB(71) = itmp

C  copy secondary particles
C  (translate PDG-ID to CPC and sort according to CPC)
      ichan = 0
      do i=1,i_tab_max
        if(idec_list(1,i).ne.0) then
          do j=idec_list(2,i),idec_list(3,i)
            ichan = ichan+1
            wg_sec_list(ichan) = wg_chan(j)
            do k=1,3
              if(isec_linear(3*(j-1)+k).ne.0) then
                isec_list(k,ichan) = IPHO_PDG2ID(isec_linear(3*(j-1)+k))
              else
                isec_list(k,ichan) = 0
              endif
            enddo
          enddo
        endif
      enddo

C  add two-pion background (low-mass photon dissociation)
      i = IPHO_PDG2ID(92)
      ichan = ichan+1
      idec_list(1,i) = 1
      idec_list(2,i) = ichan
      idec_list(3,i) = ichan
      wg_sec_list(ichan) = 1.D0
      isec_list(1,ichan) = IPHO_PDG2ID(211)
      isec_list(2,ichan) = IPHO_PDG2ID(-211)
      isec_list(3,ichan) = 0

C  min. mass limits for strings: q-qbar
      do i=1,6
        do j=1,6
          AM2P = 1000.D0
          AM2V = 1000.D0
          do k=1,3
C  pseudo-scalar mesons
            i1 = abs(id_psm_list(i,k))
            if(i1.ne.0) then
              AM1 = xm_list(i1)
            else
              AM1 = PHO_PMASS(i,3)+PHO_PMASS(k,3)
            endif
            i2 = abs(id_psm_list(k,j))
            if(i2.ne.0) then
              AM2 = xm_list(i2)
            else
              AM2 = PHO_PMASS(k,3)+PHO_PMASS(j,3)
            endif
            AM2P = MIN(AM2P,AM1+AM2)
C  vector mesons
            i1 = abs(id_vem_list(i,k))
            if(i1.ne.0) then
              AM1 = xm_list(i1)
            else
              AM1 = PHO_PMASS(i,3)+PHO_PMASS(k,3)
            endif
            i2 = abs(id_vem_list(k,j))
            if(i2.ne.0) then
              AM2 = xm_list(i2)
            else
              AM2 = PHO_PMASS(k,3)+PHO_PMASS(j,3)
            endif
            AM2V = MIN(AM2V,AM1+AM2)
          enddo
          xm_psm2_list(i,j) = AM2P
          xm_vem2_list(i,j) = AM2V
        enddo
      enddo

C  min. mass limits for strings: qq-q
      do i=1,6
        do j=1,6
          do k=1,6
            AM82  = 1000.D0
            AM102 = 1000.D0
            do l=1,3
C  pseudo-scalar meson
              i1 = abs(id_psm_list(k,l))
              if(i1.ne.0) then
                AM1 = xm_list(i1)
              else
                AM1 = PHO_PMASS(i,3)+PHO_PMASS(k,3)
              endif
C  vector meson
              i2 = abs(id_vem_list(k,l))
              if(i2.ne.0) then
                AM2 = xm_list(i2)
              else
                AM2 = PHO_PMASS(i,3)+PHO_PMASS(k,3)
              endif
C  octet baryon
              AMM = min(AM1,AM2)
              K8  = id_b8_list(i,j,l)
              if(K8.ne.0) then
                AM1 = xm_list(K8)
              else
                AM1 = PHO_PMASS(i,3)+PHO_PMASS(j,3)+PHO_PMASS(l,3)
              endif
              AM82  = MIN(AM82, AM1 + AMM)
C  decuplet baryon
              K10 = id_b10_list(i,j,l)
              if(K10.ne.0) then
                AM2 = xm_list(K10)
              else
                AM2 = PHO_PMASS(i,3)+PHO_PMASS(j,3)+PHO_PMASS(l,3)
              endif
              AM102 = MIN(AM102, AM2 + AMM)
            enddo
            xm_b82_list(i,j,k)  = AM82
            xm_b102_list(i,j,k) = AM102
          enddo
        enddo
      enddo

C  min. mass limits for strings: qq-qbarqbar
      do i=1,6
        do j=1,6
          do ii=1,6
            do jj=1,6
              AM82  = 1000.D0
              AM102 = 1000.D0
              do l=1,3
C  octet baryons
                K8  = id_b8_list(i,j,l)
                if(K8.ne.0) then
                  AM1 = xm_list(K8)
                else
                  AM1 = PHO_PMASS(i,3)+PHO_PMASS(j,3)+PHO_PMASS(l,3)
                endif
                L8  = id_b8_list(ii,jj,l)
                if(L8.ne.0) then
                  AM2 = xm_list(L8)
                else
                  AM2 = PHO_PMASS(ii,3)+PHO_PMASS(jj,3)+PHO_PMASS(l,3)
                endif
                AM82  = MIN(AM82, AM1+AM2)
C  decuplet baryons
                K10 = id_b10_list(i,j,l)
                if(K10.ne.0) then
                  AM1 = xm_list(K10)
                else
                  AM1 = PHO_PMASS(i,3)+PHO_PMASS(j,3)+PHO_PMASS(l,3)
                endif
                L10 = id_b10_list(ii,jj,l)
                if(L10.ne.0) then
                  AM2 = xm_list(L10)
                else
                  AM2 = PHO_PMASS(ii,3)+PHO_PMASS(jj,3)+PHO_PMASS(l,3)
                endif
                AM102 = MIN(AM102, AM1+AM2)
              enddo
              xm_bb82_list(i,j,ii,jj)  = AM82
              xm_bb102_list(i,j,ii,jj) = AM102
            enddo
          enddo
        enddo
      enddo

      END


 
 


CDECK  ID>, PHO_PARREM
      SUBROUTINE PHO_PARREM(INDX,IOUT,IREM,IREJ)
C**********************************************************************
C
C     selection of particle remnant flavour(s) (quark or diquark)
C
C     input:    INDX   index of particle in /POEVT1/
C               IOUT   parton which was taken out
C
C     output:   IREM   remnant according to valence flavours
C               IREJ   0  flavour combination possible
C                      1  flavour combination impossible
C
C     all particle ID are given according to PDG conventions
C
C**********************************************************************

      IMPLICIT NONE
      SAVE

      INTEGER INDX,IOUT,IREM,IREJ

C  input/output channels
      INTEGER LI,LO ,LPRI
      COMMON /POINOU/ LI,LO ,LPRI

C  event debugging information
      INTEGER NMAXD
      PARAMETER (NMAXD=100)
      INTEGER IDEB,KSPOM,KHPOM,KSREG,KHDIR,KACCEP,KSTRG,KHTRG,KSLOO,
     &        KHLOO,KSDPO,KHDPO,KEVENT,KSOFT,KHARD
      COMMON /PODEBG/ IDEB(NMAXD),KSPOM,KHPOM,KSREG,KHDIR,KACCEP,KSTRG,
     &                KHTRG,KSLOO,KHLOO,KSDPO,KHDPO,KEVENT,KSOFT,KHARD

C  standard particle data interface
      INTEGER NMXHEP
      PARAMETER (NMXHEP=8000)
      INTEGER NEVHEP,NHEP,ISTHEP,IDHEP,JMOHEP,JDAHEP
      DOUBLE PRECISION PHEP,VHEP
      COMMON /POEVT1/ NEVHEP,NHEP,ISTHEP(NMXHEP),IDHEP(NMXHEP),
     &                JMOHEP(2,NMXHEP),JDAHEP(2,NMXHEP),PHEP(5,NMXHEP),
     &                VHEP(4,NMXHEP)

C  extension to standard particle data interface (PHOJET specific)
      INTEGER IMPART,IPHIST,ICOLOR
      COMMON /POEVT2/ IMPART(NMXHEP),IPHIST(2,NMXHEP),ICOLOR(2,NMXHEP)

C  general particle data
      DOUBLE PRECISION xm_list,tau_list,gam_list,
     &  xm_psm2_list,xm_vem2_list,xm_b82_list,xm_b102_list,
     &  xm_bb82_list,xm_bb102_list
      INTEGER          ich3_list,iba3_list,iq_list,
     &                 id_psm_list,id_vem_list,id_b8_list,id_b10_list
      COMMON /POPAR2/ xm_list(300),tau_list(300),gam_list(300),
     &  xm_psm2_list(6,6),xm_vem2_list(6,6),
     &  xm_b82_list(6,6,6),xm_b102_list(6,6,6),
     &  xm_bb82_list(6,6,6,6),xm_bb102_list(6,6,6,6),
     &  ich3_list(300),iba3_list(300),iq_list(3,300),
     &  id_psm_list(6,6),id_vem_list(6,6),
     &  id_b8_list(6,6,6),id_b10_list(6,6,6)


C  external functions
      INTEGER IPHO_DIQU

C  local variables
      INTEGER ID,IS,ID1,ID2,i,k,K1,K2,IQUA,IDQ
      DIMENSION IQUA(3),IDQ(2)

      ID1 = IDHEP(INDX)
      ID2 = IMPART(INDX)
      IREJ = 0

      IF(ID2.EQ.0) THEN
        IF (LPRI.GT.4)
     &  WRITE(LO,'(1X,A,I6)') 'PHO_PARREM: no CPC ID available for',INDX
        CALL PHO_ABORT
      ENDIF

C**anfe more general treatment of particles with flavor mixing
      If ((id1==22) .Or. (id1==990) .Or. (id1==110)) Then
C  photon, pomeron or reggeon
        IREM = -IOUT
        GOTO 100
      End If

C  ordinary hadron
      id = abs(id2)
      is = sign(1, id2)
      iqua(1) = iq_list(1, id)*is
      iqua(2) = iq_list(2, id)*is
      iqua(3) = iq_list(3, id)*is

      If ((iqua(3) == 0) .And. (iqua(2) == -iqua(1))) Then
C meson with flavor mixing
         irem = -iout
         IF((LPRI.GT.4).AND.(IDEB(72).GE.10)) Then
            Write(6,*) 'PHO_PARREM: particle has flavor mixing ',
     &                  id, iqua(1), iqua(2), irem
         End If
         Go To 100
      End If


C  compare to flavour content
      IF(ABS(IOUT).LT.1000) THEN
C  single quark requested
        IF(IQUA(1).EQ.IOUT) THEN
          K1 = 2
          K2 = 3
        ELSE IF(IQUA(2).EQ.IOUT) THEN
          K1 = 1
          K2 = 3
        ELSE IF(IQUA(3).EQ.IOUT) THEN
          K1 = 1
          K2 = 2
        ELSE
          GOTO 150
        ENDIF
        IF(IQUA(3).EQ.0) THEN
          IREM = IQUA(K1)
        ELSE
          IREM = IPHO_DIQU(IQUA(K1),IQUA(K2))
        ENDIF
      ELSE IF(IQUA(3).NE.0) THEN
C  diquark requested from baryon
        IDQ(1) = IOUT/1000
        IDQ(2) = (IOUT-IDQ(1)*1000)/100
        do i=1,2
          do k=1,3
            if(IDQ(i).eq.IQUA(k)) then
              IQUA(k) = 0
              goto 110
            endif
          enddo
          goto 150
 110      continue
        enddo
        IREM = IQUA(1)+IQUA(2)+IQUA(3)
      ENDIF

 100  CONTINUE
C  debug output
      IF (LPRI.GT.4 .AND. IDEB(72).GE.10)
     &WRITE(LO,'(1X,A,5I6)')
     &  'PHO_PARREM: INDX,ID-PDG,ID-BAM,IOUT,IREM',
     &  INDX,ID1,ID2,IOUT,IREM
      RETURN

C  rejection
 150  CONTINUE
      IREJ = 1
      IF (LPRI.GT.4 .AND. IDEB(72).GE.2)
     &WRITE(LO,'(1X,A,5I7)')
     &  'PHO_PARREM: rejection IDPDG,Q1-3,IOUT',IDHEP(INDX),IQUA,IOUT

      END


 
 


CDECK  ID>, PHO_PARTON
      SUBROUTINE PHO_PARTON(IPROC,JM1,JM2,P1,P2,IREJ)
C********************************************************************
C
C     calculation of complete parton configuration
C
C     input:  IPROC   process ID  1 nondiffractive
C                                 2 elastic
C                                 3 quasi-ela. rho,omega,phi prod.
C                                 4 double Pomeron
C                                 5 single diff 1
C                                 6 single diff 2
C                                 7 double diff diss.
C                                 8 single-resolved / direct photon
C             JM1,2   index of mother particles in /POEVT1/
C
C
C     output: complete parton configuration in /POEVT1/
C             IREJ                1 failure
C                                 0 success
C                                50 rejection due to user cutoffs
C
C********************************************************************
      IMPLICIT DOUBLE PRECISION (A-H,O-Z)
      SAVE

      DIMENSION P1(4),P2(4)

      PARAMETER ( TINY   =  1.D-10 )

C  input/output channels
      INTEGER LI,LO ,LPRI
      COMMON /POINOU/ LI,LO ,LPRI

C  event debugging information
      INTEGER NMAXD
      PARAMETER (NMAXD=100)
      INTEGER IDEB,KSPOM,KHPOM,KSREG,KHDIR,KACCEP,KSTRG,KHTRG,KSLOO,
     &        KHLOO,KSDPO,KHDPO,KEVENT,KSOFT,KHARD
      COMMON /PODEBG/ IDEB(NMAXD),KSPOM,KHPOM,KSREG,KHDIR,KACCEP,KSTRG,
     &                KHTRG,KSLOO,KHLOO,KSDPO,KHDPO,KEVENT,KSOFT,KHARD

C  model switches and parameters
      CHARACTER*8 MDLNA
      INTEGER ISWMDL,IPAMDL
      DOUBLE PRECISION PARMDL
      COMMON /POMDLS/ MDLNA(50),ISWMDL(50),PARMDL(400),IPAMDL(400)

C  table of particle indices for recursive PHOJET calls
      INTEGER MAXIPX
      PARAMETER ( MAXIPX = 500 )
      INTEGER IPOPOS,IPORES,IPOIX1,IPOIX2,IPOIX3
      COMMON /PORECU/ IPOPOS(2,MAXIPX),IPORES(MAXIPX),
     &                IPOIX1,IPOIX2,IPOIX3

C  general process information
      INTEGER IPROCE,IDNODF,IDIFR1,IDIFR2,IDDPOM,IPRON
      COMMON /POPRCS/ IPROCE,IDNODF,IDIFR1,IDIFR2,IDDPOM,IPRON(15,4)

C  global event kinematics and particle IDs
      INTEGER IFPAP,IFPAB
      DOUBLE PRECISION ECM,PCM,PMASS,PVIRT
      COMMON /POGCMS/ ECM,PCM,PMASS(2),PVIRT(2),IFPAP(2),IFPAB(2)

C  cross sections
      INTEGER IPFIL,IFAFIL,IFBFIL
      DOUBLE PRECISION SIGTOT,SIGELA,SIGVM,SIGINE,SIGNDF,SIGDIR,
     &                 SIGLSD,SIGHSD,SIGLDD,SIGHDD,SIGCDF,
     &                 SIGPOM,SIGREG,SIGHAR,SIGTR1,SIGTR2,SIGLOO,
     &                 SIGDPO,SIG1SO,SIG1HA,SLOEL,SLOVM,SIGCOR,
     &                 FSUP,FSUD,FSUH,ECMFIL,P2AFIL,P2BFIL
      COMMON /POCSEC/ SIGTOT,SIGELA,SIGVM(0:4,0:4),SIGINE,SIGNDF,SIGDIR,
     &                SIGLSD(2),SIGHSD(2),SIGLDD,SIGHDD,SIGCDF(0:4),
     &                SIGPOM,SIGREG,SIGHAR,SIGTR1(2),SIGTR2(2),SIGLOO,
     &                SIGDPO(4),SIG1SO,SIG1HA,SLOEL,SLOVM(4,4),SIGCOR,
     &                FSUP(2),FSUD(2),FSUH(2),ECMFIL,P2AFIL,P2BFIL,
     &                IPFIL,IFAFIL,IFBFIL

C  event weights and generated cross section
      INTEGER IPOWGC,ISWCUT,IVWGHT
      DOUBLE PRECISION SIGGEN,HSWGHT,HSWCUT,EVWGHT
      COMMON /POWGHT/ SIGGEN(4),HSWGHT(0:10),HSWCUT(20),EVWGHT(0:10),
     &                IPOWGC(0:10),ISWCUT(20),IVWGHT(0:10)

C  internal rejection counters
      INTEGER NMXJ
      PARAMETER (NMXJ=60)
      CHARACTER*10 REJTIT
      INTEGER IFAIL
      COMMON /POLOOP/ IFAIL(NMXJ),REJTIT(NMXJ)


      IREJ = 0
C  clear event statistics
      KSPOM = 0
      KHPOM = 0
      KSREG = 0
      KHDIR = 0
      KSTRG = 0
      KHTRG = 0
      KSLOO = 0
      KHLOO = 0
      KHARD = 0
      KSOFT = 0
      KSDPO = 0
      KHDPO = 0

C-------------------------------------------------------------------
C  nondiffractive resolved processes

      IF(IPROC.EQ.1) THEN
C  sample number of interactions
C555    CONTINUE
        IINT = 0
        IP   = 1
C  generate only hard events
        IF(ISWMDL(2).EQ.0) THEN
          MHPOM = 1
          MSPOM = 0
          MSREG = 0
          MHDIR = 0
          HSWGHT(1) = 1.D0
        ELSE
C  minimum bias events
          IPOWGC(1) = 0
 10       CONTINUE
          CALL PHO_SAMPRB(ECM,IP,IINT,JINT,KINT)
          IPOWGC(1) = IPOWGC(1)+1
          MINT = 0
          MHDIR = 0
          MSTRG = 0
          MSLOO = 0
C
C  resolved soft processes: pomeron and reggeon
          MSPOM = IINT
          MSREG = JINT
C  resolved hard process: hard pomeron
          MHPOM = KINT
C  resolved absorptive corrections
          MPTRI = 0
          MPLOO = 0
C  restrictions given by user
          IF(MSPOM.LT.ISWCUT(1)) GOTO 10
          IF(MSREG.LT.ISWCUT(2)) GOTO 10
          IF(MHPOM.LT.ISWCUT(3)) GOTO 10
          HSWGHT(1) = 1.D0/DBLE(IPOWGC(1))
C  ----------------------------
          IF(ISWMDL(15).EQ.0) THEN
            MHPOM = 0
            IF(MSREG.GT.0) THEN
              MSPOM = 0
              MSREG = 1
            ELSE
              MSPOM = 1
              MSREG = 0
            ENDIF
          ELSE IF(ISWMDL(15).EQ.1) THEN
            IF(MHPOM.GT.0) THEN
              MHPOM = 1
              MSPOM = 0
              MSREG = 0
            ELSE IF(MSPOM.GT.0) THEN
              MSPOM = 1
              MSREG = 0
            ELSE
              MSREG = 1
            ENDIF
          ELSE IF(ISWMDL(15).EQ.2) THEN
            MHPOM = MIN(1,MHPOM)
          ELSE IF(ISWMDL(15).EQ.3) THEN
            MSPOM = MIN(1,MSPOM)
          ENDIF
        ENDIF
C  ----------------------------

C  statistics
        ISPS = ISPS+MSPOM
        IHPS = IHPS+MHPOM
        ISRS = ISRS+MSREG
        ISTS = ISTS+MSTRG
        ISLS = ISLS+MSLOO

        IF (LPRI.GT.4 .AND. IDEB(3).GE.5)
     &  WRITE(LO,'(1X,A,I10,I7,6I4)')
     &    'PHO_PARTON: EV,SP,SR,HP,HD,ET,EL',
     &    KEVENT,MSPOM,MSREG,MHPOM,MHDIR,MPTRI,MPLOO

        ITRY2 = 0
 50     CONTINUE
        ITRY2 = ITRY2+1
        IF(ITRY2.GT.1) CALL PHO_EVEINI(2,P1,P2,JM1,JM2)
        KSPOM = MSPOM
        KSREG = MSREG
        KHPOM = MHPOM
        KHDIR = MHDIR
        KSTRG = MPTRI
        KSLOO = MPLOO

        CALL PHO_STDPAR(JM1,JM2,1,MSPOM,MSREG,MHPOM,MHDIR,IREJ)
        IF(IREJ.NE.0) THEN
          IF(IREJ.EQ.50) RETURN
          IF(IDEB(3).GE.2) THEN
            IF (LPRI.GT.4)
     &      WRITE(LO,'(/1X,A,I5)')
     &        'PHO_PARTON: rejection by PHO_STDPAR ',ITRY2
            CALL PHO_PREVNT(-1)
          ENDIF
          RETURN
        ENDIF
        IF(MHPOM.GT.0) THEN
          IDNODF = 3
        ELSE IF(MSPOM.GT.0) THEN
          IDNODF = 2
        ELSE
          IDNODF = 1
        ENDIF
C  check of quantum numbers of parton configurations
        IF(IDEB(3).GE.0) THEN
          CALL PHO_CHECK(1,IREJ)
          IF(IREJ.NE.0) GOTO 50
        ENDIF
C  sample strings to prepare fragmentation
        CALL PHO_STRING(1,IREJ)
        IF(IREJ.NE.0) THEN
          IF(IREJ.EQ.50) RETURN
          IFAIL(30) = IFAIL(30)+1
          IF(IDEB(3).GE.2)  THEN
            IF (LPRI.GT.4)
     &      WRITE(LO,'(/1X,A,I5)')
     &        'PHO_PARTON: rejection by PHO_STRING',ITRY2
            CALL PHO_PREVNT(-1)
          ENDIF
          IF(ITRY2.LT.20) GOTO 50
          IF(IDEB(3).GE.1) THEN
            IF (LPRI.GT.4)
     &      WRITE(LO,'(/1X,A,I5)')
     &        'PHO_PARTON: rejection',ITRY2
            CALL PHO_PREVNT(-1)
          ENDIF
          RETURN
        ENDIF

C  statistics
        ISPA = ISPA+KSPOM
        IHPA = IHPA+KHPOM
        ISRA = ISRA+KSREG
        ISTA = ISTA+KSTRG
        ISLA = ISLA+KSLOO

C-------------------------------------------------------------------
C  elastic scattering / quasi-elastic rho/omega/phi production

      ELSE IF((IPROC.EQ.2).OR.(IPROC.EQ.3)) THEN
        IF (LPRI.GT.4 .AND. IDEB(3).GE.5)
     &  WRITE(LO,'(1X,A,I10,I4)')
     &    'PHO_PARTON: ela./q-ela.sca:(EV,IPROC)',KEVENT,IPROC

C  DPMJET call with special projectile / target: transform into CMS
        IF((IPAMDL(13).GT.0).AND.(IPOIX3.EQ.0))
     &    CALL PHO_DFWRAP(1,JM1,JM2)

        CALL PHO_QELAST(IPROC,JM1,JM2,IREJ)

        IF(IREJ.NE.0) THEN
C  DPMJET call with special projectile / target: clean up
          IF((IPAMDL(13).GT.0).AND.(IPOIX3.EQ.0))
     &      CALL PHO_DFWRAP(-2,JM1,JM2)
          IF(IDEB(3).GE.2) THEN
            IF (LPRI.GT.4)
     &      WRITE(LO,'(/1X,A,I5)')
     &        'PHO_PARTON: rejection by PHO_QELAST',IREJ
            CALL PHO_PREVNT(-1)
          ENDIF
          RETURN
        ENDIF

C  DPMJET call with special projectile / target: transform back
        IF((IPAMDL(13).GT.0).AND.(IPOIX3.EQ.0))
     &    CALL PHO_DFWRAP(2,JM1,JM2)

C  prepare possible decays
        CALL PHO_STRING(1,IREJ)
        IF(IREJ.NE.0) THEN
          IF(IREJ.EQ.50) RETURN
          IFAIL(30) = IFAIL(30)+1
          RETURN
        ENDIF

C---------------------------------------------------------------------
C  double Pomeron scattering

      ELSE IF(IPROC.EQ.4) THEN
        MSOFT = 0
        MHARD = 0
        IF (LPRI.GT.4 .AND. IDEB(3).GE.5)
     &  WRITE(LO,'(1X,A,I10)')
     &      'PHO_PARTON: EV,double-pomeron scattering',KEVENT
        IDPS = IDPS+1
        ITRY2 = 0
 60     CONTINUE
        ITRY2 = ITRY2+1
        IF(ITRY2.GT.1) CALL PHO_EVEINI(2,P1,P2,JM1,JM2)
C
        CALL PHO_CDIFF(JM1,JM2,MSOFT,MHARD,1,IREJ)
        IF(IREJ.NE.0) THEN
          IF(IDEB(3).GE.2) THEN
            IF (LPRI.GT.4)
     &      WRITE(LO,'(/1X,A,I5)')
     &        'PHO_PARTON: rejection by PHO_CDIFF',IREJ
            CALL PHO_PREVNT(-1)
          ENDIF
          RETURN
        ENDIF
C  check of quantum numbers of parton configurations
        IF(IDEB(3).GE.0) THEN
          CALL PHO_CHECK(1,IREJ)
          IF(IREJ.NE.0) GOTO 60
        ENDIF
C  sample strings to prepare fragmentation
        CALL PHO_STRING(1,IREJ)
        IF(IREJ.NE.0) THEN
          IF(IREJ.EQ.50) RETURN
          IFAIL(30) = IFAIL(30)+1
          IF(IDEB(3).GE.2) THEN
            IF (LPRI.GT.4)
     &      WRITE(LO,'(/1X,A,I5)')
     &        'PHO_PARTON: rejection by PHO_STRING',ITRY2
            CALL PHO_PREVNT(-1)
          ENDIF
          IF(ITRY2.LT.10) GOTO 60
          IF (LPRI.GT.4)
     &    WRITE(LO,'(/1X,A,I5)') 'PHO_PARTON: rejection',ITRY2
          CALL PHO_PREVNT(-1)
          RETURN
        ENDIF
        IDPA = IDPA+1

C-----------------------------------------------------------------------
C  single / double diffraction dissociation

      ELSE IF((IPROC.GE.5).AND.(IPROC.LE.7)) THEN
        MSOFT = 0
        MHARD = 0
        IF(IPROC.EQ.5) ID1S = ID1S+1
        IF(IPROC.EQ.6) ID2S = ID2S+1
        IF(IPROC.EQ.7) ID3S = ID3S+1
        ITRY2 = 0
 70     CONTINUE
        ITRY2 = ITRY2+1
        IF(ITRY2.GT.1) CALL PHO_EVEINI(2,P1,P2,JM1,JM2)
        IPAR1 = 1
        IPAR2 = 1
        IF(IPROC.EQ.5) IPAR2 = 0
        IF(IPROC.EQ.6) IPAR1 = 0
        IF (LPRI.GT.4 .AND. IDEB(3).GE.5)
     &  Write (lo, '(1X,A,I10,3I4)') 
     &     'PHO_PARTON: diffraction, EV, IPROC, IPAR1, IPAR2',
     &     KEVENT, IPROC, IPAR1, IPAR2      
C  calculate rapidity gap survival probability
        SPROB = 1.D0
        IF(ECM.GT.10.D0) THEN
          IF((IPAR1.GE.1).AND.(IPAR2.EQ.0)) THEN
            IF(SIGTR1(1).LT.1.D-10) THEN
              SPROB = 1.D0
            ELSE
              SPROB = SIGHSD(1)/(SIGTR1(1)-2.D0*(SIGDPO(1)+SIGDPO(2)))
            ENDIF
          ELSE IF((IPAR1.EQ.0).AND.(IPAR2.GE.1)) THEN
            IF(SIGTR2(1).LT.1.D-10) THEN
              SPROB = 1.D0
            ELSE
              SPROB = SIGHSD(2)/(SIGTR2(1)-2.D0*(SIGDPO(1)+SIGDPO(3)))
            ENDIF
          ELSE IF((IPAR1.GE.1).AND.(IPAR2.GE.1)) THEN
            IF(SIGLOO.LT.1.D-10) THEN
              SPROB = 1.D0
            ELSE
              SPROB = SIGHDD/SIGLOO
            ENDIF
          ENDIF
        ENDIF

**sr
* temporary patch, r.e. 8.6.99
        IF(IPAMDL(13).GT.0) Then
          IF(LPRI.GT.4 .AND. IDEB(3).GE.15)
     &      Write (lo, '(1X,A)') 
     &        'PHO_PARTON: SPROB set to 1. (DPMJET temporary??)'
          SPROB = 1.D0
        END IF

C  DPMJET call with special projectile / target: transform into CMS
        IF((IPAMDL(13).GT.0).AND.(IPOIX3.EQ.0))
     &    CALL PHO_DFWRAP(1,JM1,JM2)

        CALL PHO_DIFDIS(IPAR1,IPAR2,JM1,JM2,SPROB,0,MSOFT,MHARD,IREJ)

        IF(IREJ.NE.0) THEN
C  DPMJET call with special projectile / target: clean up
          IF((IPAMDL(13).GT.0).AND.(IPOIX3.EQ.0))
     &      CALL PHO_DFWRAP(-2,JM1,JM2)
          IF(IDEB(3).GE.2) THEN
            IF (LPRI.GT.4)
     .      WRITE(LO,'(/1X,A,I5)')
     &        'PHO_PARTON: rejection by PHO_DIFDIS',IREJ
            CALL PHO_PREVNT(-1)
          ENDIF
          RETURN
        ENDIF

C  DPMJET call with special projectile / target: transform back
        IF((IPAMDL(13).GT.0).AND.(IPOIX3.EQ.0))
     &    CALL PHO_DFWRAP(2,JM1,JM2)

C  check of quantum numbers of parton configurations
        IF(IDEB(3).GE.0) THEN
          CALL PHO_CHECK(1,IREJ)
          IF(IREJ.NE.0) GOTO 70
        ENDIF
C  sample strings to prepare fragmentation
        CALL PHO_STRING(1,IREJ)
        IF(IREJ.NE.0) THEN
          IF(IREJ.EQ.50) RETURN
          IFAIL(30) = IFAIL(30)+1
          IF(IDEB(3).GE.2) THEN
            IF (LPRI.GT.4)
     &      WRITE(LO,'(/1X,A,I5)')
     &        'PHO_PARTON: rejection by PHO_STRING',ITRY2
            CALL PHO_PREVNT(-1)
          ENDIF
          IF(ITRY2.LT.10) GOTO 70
          IF (LPRI.GT.4)
     &    WRITE(LO,'(/1X,A,I5)')
     &      'PHO_PARTON: rejection',ITRY2
          CALL PHO_PREVNT(-1)
          RETURN
        ENDIF
        IF(IPROC.EQ.5) ID1A = ID1A+1
        IF(IPROC.EQ.6) ID2A = ID2A+1
        IF(IPROC.EQ.7) ID3A = ID3A+1

C-----------------------------------------------------------------------
C  single / double direct processes

      ELSE IF(IPROC.EQ.8) THEN
        MSREG = 0
        MSPOM = 0
        MHPOM = 0
        MHDIR = 1
        IF(IDEB(3).GE.5) THEN
          IF (LPRI.GT.4)
     &    WRITE(LO,'(1X,A,I10)') 'PHO_PARTON: EV,direct proc',KEVENT
        ENDIF
        IDIS = IDIS+MHDIR
        ITRY2 = 0
 80     CONTINUE
        ITRY2 = ITRY2+1
        IF(ITRY2.GT.1) CALL PHO_EVEINI(2,P1,P2,JM1,JM2)
        KSPOM = MSPOM
        KSREG = MSREG
        KHPOM = MHPOM
        KHDIR = 4

        CALL PHO_STDPAR(JM1,JM2,1,MSPOM,MSREG,MHPOM,MHDIR,IREJ)
        IF(IREJ.NE.0) THEN
          IF(IREJ.EQ.50) RETURN
          IF(IDEB(3).GE.2) THEN
            IF (LPRI.GT.4)
     &      WRITE(LO,'(/1X,A,I5)')
     &        'PHO_PARTON: rejection by PHO_STDPAR',ITRY2
            CALL PHO_PREVNT(-1)
          ENDIF
          RETURN
        ENDIF
        IDNODF = 4
C  check of quantum numbers of parton configurations
        IF(IDEB(3).GE.0) THEN
          CALL PHO_CHECK(1,IREJ)
          IF(IREJ.NE.0) GOTO 80
        ENDIF
C  sample strings to prepare fragmentation
        CALL PHO_STRING(1,IREJ)
        IF(IREJ.NE.0) THEN
          IF(IREJ.EQ.50) RETURN
          IFAIL(30) = IFAIL(30)+1
          IF(IDEB(3).GE.2) THEN
            IF (LPRI.GT.4)
     &      WRITE(LO,'(/1X,A,I5)')
     &        'PHO_PARTON: rejection by PHO_STRING',ITRY2
            CALL PHO_PREVNT(-1)
          ENDIF
          IF(ITRY2.LT.10) GOTO 80
          IF (LPRI.GT.4)
     &    WRITE(LO,'(/1X,A,I5)') 'PHO_PARTON: rejection',ITRY2
          CALL PHO_PREVNT(-1)
          RETURN
        ENDIF
        IF(IPROC.EQ.5) ID1A = ID1A+1
        IF(IPROC.EQ.6) ID2A = ID2A+1
        IF(IPROC.EQ.7) ID3A = ID3A+1
        IDIA = IDIA+MHDIR

C-----------------------------------------------------------------------
C  initialize control statistics

      ELSE IF(IPROC.EQ.-1) THEN
        CALL PHO_SAMPRB(ECM,-1,0,0,0)
        CALL PHO_STDPAR(-1,0,0,0,0,0,0,IREJ)
        CALL PHO_SEAFLA(-1,0,0,DUM)
        IF((IFPAP(1).EQ.22).OR.(IFPAP(2).EQ.22))
     &    CALL PHO_QELAST(-1,1,2,0)
        ISPS = 0
        ISPA = 0
        ISRS = 0
        ISRA = 0
        IHPS = 0
        IHPA = 0
        ISTS = 0
        ISTA = 0
        ISLS = 0
        ISLA = 0
        ID1S = 0
        ID1A = 0
        ID2S = 0
        ID2A = 0
        ID3S = 0
        ID3A = 0
        IDPS = 0
        IDPA = 0
        IDIS = 0
        IDIA = 0
        CALL PHO_STRING(-1,IREJ)
        CALL PHO_DIFDIS(0,0,0,0,0.D0,-1,0,0,IREJ)
        RETURN

C-----------------------------------------------------------------------
C  produce statistics summary

      ELSE IF(IPROC.EQ.-2) THEN
        IF(ISWMDL(2).NE.0) CALL PHO_SAMPRB(ECM,-2,0,0,0)
        IF(IDEB(3).GE.0) THEN
          IF (LPRI.GT.4)
     &    WRITE(LO,'(/1X,A,/1X,A)')
     &      'PHO_PARTON: internal statistics on parton configurations',
     &      '--------------------------------------------------------'
          IF (LPRI.GT.4)
     &    WRITE(LO,'(5X,A)') 'process          sampled      accepted'
          IF (LPRI.GT.4)
     &    WRITE(LO,'(5X,A,2I12)') 'soft pom.',ISPS,ISPA
          IF (LPRI.GT.4)
     &    WRITE(LO,'(5X,A,2I12)') 'hard pom.',IHPS,IHPA
          IF (LPRI.GT.4)
     &    WRITE(LO,'(5X,A,2I12)') 'soft reg.',ISRS,ISRA
          IF (LPRI.GT.4)
     &    WRITE(LO,'(5X,A,2I12)') 'enh. tri.',ISTS,ISTA
          IF (LPRI.GT.4)
     &    WRITE(LO,'(5X,A,2I12)') 'enh. loo.',ISLS,ISLA
          IF (LPRI.GT.4)
     &    WRITE(LO,'(5X,A,2I12)') 'diff.pa1.',ID1S,ID1A
          IF (LPRI.GT.4)
     &    WRITE(LO,'(5X,A,2I12)') 'diff.pa2.',ID2S,ID2A
          IF (LPRI.GT.4)
     &    WRITE(LO,'(5X,A,2I12)') 'doub.dif.',ID3S,ID3A
          IF (LPRI.GT.4)
     &    WRITE(LO,'(5X,A,2I12)') 'doub.pom.',IDPS,IDPA
          IF (LPRI.GT.4)
     &    WRITE(LO,'(5X,A,2I12/)') 'dir.phot.',IDIS,IDIA
        ENDIF
        CALL PHO_STDPAR(-2,0,0,0,0,0,0,IREJ)
        IF((IFPAP(1).EQ.22).OR.(IFPAP(2).EQ.22))
     &    CALL PHO_QELAST(-2,1,2,0)
        CALL PHO_STRING(-2,IREJ)
        CALL PHO_DIFDIS(0,0,0,0,0.D0,-2,0,0,IREJ)
        CALL PHO_SEAFLA(-2,0,0,DUM)
        RETURN
      ELSE
        IF (LPRI.GT.4)
     &  WRITE(LO,'(1X,A,I2)')
     &    'PHO_PARTON:ERROR: unknown process ID ',IPROC
        STOP
      ENDIF

      END


      DOUBLE PRECISION FUNCTION PHO_PARTONX12 (IPRTN, XX, QQ)

c  Given the parton distribution function in the array U in
c  COMMON / PEVLDT / , this routine interpolates to find
c  the parton distribution at an arbitray point in x and q.
c
      IMPLICIT DOUBLE PRECISION (A-H,O-Z)
      SAVE

C  input/output channels
      INTEGER LI,LO ,LPRI
      COMMON /POINOU/ LI,LO ,LPRI

      PARAMETER (MXX = 201, MXQ = 40, MXF = 6, MaxVal=4)
      PARAMETER (MXPQX = (MXF+1+MaxVal) * MXQ * MXX)

      COMMON
     > / CtqPar1 / qBase, XV(0:MXX), TV(0:MXQ),UPD(MXPQX),AlsCTEQ(0:mxq)
     > / CtqPar2 / Nx, Nt, NfMx, MxVal
     > / XQrange / Qini, Qmax, Xmin
     > /Setchange/ Isetch, ipdsset, ipdsformat

      DIMENSION fvec(4), fij(4)
      DIMENSION xvpow(0:mxx)
      DATA OneP / 1.00001 /
      DATA xpow / 0.3d0 /       !**** choice of interpolation variable
      DATA nqvec / 4 /
      DATA ientry / 0 /
      DATA X, Q, JX, JQ /-1D0, -1D0, 0, 0/
      SAVE xvpow
      SAVE X, Q, JX, JQ, JLX, JLQ
      SAVE ss, const1, const2, const3, const4, const5, const6
      SAVE sy2, sy3, s23, tt, t12, t13, t23, t24, t34, ty2, ty3
      SAVE tmp1, tmp2, tdet

c store the powers used for interpolation on first call...
      if(Isetch .eq. 1) then
         Isetch = 0

         xvpow(0) = 0D0
         do i = 1, nx
            xvpow(i) = xv(i)**xpow
         enddo
      elseIf((XX.eq.X).and.(QQ.eq.Q)) then
        goto 99
      endif

      X = XX
      Q = QQ
      tt = log(log(Q/qBase))

c      -------------    find lower end of interval containing x, i.e.,
c                       get jx such that xv(jx) .le. x .le. xv(jx+1)...
      JLx = -1
      JU = Nx+1
 11   If (JU-JLx .GT. 1) Then
         JM = (JU+JLx) / 2
         If (X .Ge. XV(JM)) Then
            JLx = JM
         Else
            JU = JM
         Endif
         Goto 11
      Endif
C                     Ix    0   1   2      Jx  JLx         Nx-2     Nx
C                           |---|---|---|...|---|-x-|---|...|---|---|
C                     x     0  Xmin               x                 1
C
      If     (JLx .LE. -1) Then
        IF (LPRI.GT.4)
     &      WRITE(LOUT,'(A,1pE12.4)')
     &      'PHO_PARTONX12:Severe error: x <= 0 ! x = ',x
        Stop
      ElseIf (JLx .Eq. 0) Then
         Jx = 0
      Elseif (JLx .LE. Nx-2) Then

C                For interrior points, keep x in the middle, as shown above
         Jx = JLx - 1
      Elseif (JLx.Eq.Nx-1 .or. x.LT.OneP) Then

C                  We tolerate a slight over-shoot of one (OneP=1.00001),
C              perhaps due to roundoff or whatever, but not more than that.
C                                      Keep at least 4 points >= Jx
         Jx = JLx - 2
      Else
        IF (LPRI.GT.4)
     &      WRITE(LOUT,'(A,1pE12.4)')
     &      'PHO_PARTONX12:Severe error: x > 1 in pho_PartonX12! x = ',x
        Stop
      Endif
C          ---------- Note: JLx uniquely identifies the x-bin; Jx does not.

C                       This is the variable to be interpolated in
      ss = x**xpow

      If (JLx.Ge.2 .and. JLx.Le.Nx-2) Then

c     initiation work for "interior bins": store the lattice points in s...
      svec1 = xvpow(jx)
      svec2 = xvpow(jx+1)
      svec3 = xvpow(jx+2)
      svec4 = xvpow(jx+3)

      s12 = svec1 - svec2
      s13 = svec1 - svec3
      s23 = svec2 - svec3
      s24 = svec2 - svec4
      s34 = svec3 - svec4

      sy2 = ss - svec2
      sy3 = ss - svec3

c constants needed for interpolating in s at fixed t lattice points...
      const1 = s13/s23
      const2 = s12/s23
      const3 = s34/s23
      const4 = s24/s23
      s1213 = s12 + s13
      s2434 = s24 + s34
      sdet = s12*s34 - s1213*s2434
      tmp = sy2*sy3/sdet
      const5 = (s34*sy2-s2434*sy3)*tmp/s12
      const6 = (s1213*sy2-s12*sy3)*tmp/s34

      EndIf

c         --------------Now find lower end of interval containing Q, i.e.,
c                          get jq such that qv(jq) .le. q .le. qv(jq+1)...
      JLq = -1
      JU = NT+1
 12   If (JU-JLq .GT. 1) Then
         JM = (JU+JLq) / 2
         If (tt .GE. TV(JM)) Then
            JLq = JM
         Else
            JU = JM
         Endif
         Goto 12
       Endif

      If     (JLq .LE. 0) Then
         Jq = 0
      Elseif (JLq .LE. Nt-2) Then
C                                  keep q in the middle, as shown above
         Jq = JLq - 1
      Else
C                         JLq .GE. Nt-1 case:  Keep at least 4 points >= Jq.
        Jq = Nt - 3

      Endif
C                                   This is the interpolation variable in Q

      If (JLq.GE.1 .and. JLq.LE.Nt-2) Then
c                                        store the lattice points in t...
      tvec1 = Tv(jq)
      tvec2 = Tv(jq+1)
      tvec3 = Tv(jq+2)
      tvec4 = Tv(jq+3)

      t12 = tvec1 - tvec2
      t13 = tvec1 - tvec3
      t23 = tvec2 - tvec3
      t24 = tvec2 - tvec4
      t34 = tvec3 - tvec4

      ty2 = tt - tvec2
      ty3 = tt - tvec3

      tmp1 = t12 + t13
      tmp2 = t24 + t34

      tdet = t12*t34 - tmp1*tmp2

      EndIf


c get the pdf function values at the lattice points...

 99   If (Iprtn .Gt. MxVal) Then
         Ip = - Iprtn
      Else
         Ip = Iprtn
      EndIf
      jtmp = ((Ip + NfMx)*(NT+1)+(jq-1))*(NX+1)+jx+1

      Do it = 1, nqvec
        J1  = jtmp + it*(NX+1)

       If (Jx .Eq. 0) Then
C                      For the first 4 x points, interpolate x^2*f(x,Q)
C                      This applies to the two lowest bins JLx = 0, 1
C            We can not put the JLx.eq.1 bin into the "interrior" section
C                           (as we do for q), since Upd(J1) is undefined.
         fij(1) = 0
         fij(2) = Upd(J1+1) * XV(1)**2
         fij(3) = Upd(J1+2) * XV(2)**2
         fij(4) = Upd(J1+3) * XV(3)**2
C
C                 Use Polint which allows x to be anywhere w.r.t. the grid

         CALL PHO_POLINT4F (XVpow(0), Fij(1), ss, Fx)

         If (x .GT. 0D0)  Fvec(it) =  Fx / x**2
C                                              Pdf is undefined for x.eq.0
       ElseIf  (JLx .Eq. Nx-1) Then
C                                                This is the highest x bin:

        CALL PHO_POLINT4F (XVpow(Nx-3), Upd(J1), ss, Fx)

        Fvec(it) = Fx

       Else
C                       for all interior points, use Jon's in-line function
C                              This applied to (JLx.Ge.2 .and. JLx.Le.Nx-2)
         sf2 = Upd(J1+1)
         sf3 = Upd(J1+2)

         g1 =  sf2*const1 - sf3*const2
         g4 = -sf2*const3 + sf3*const4

         Fvec(it) = (const5*(Upd(J1)-g1)
     &               + const6*(Upd(J1+3)-g4)
     &               + sf2*sy3 - sf3*sy2) / s23

       Endif

      enddo
C                                   We now have the four values Fvec(1:4)
c     interpolate in t...

      If (JLq .LE. 0) Then
C                         1st Q-bin, as well as extrapolation to lower Q
        CALL PHO_POLINT4F (TV(0), Fvec(1), tt, ff)

      ElseIf (JLq .GE. Nt-1) Then
C                         Last Q-bin, as well as extrapolation to higher Q
        CALL PHO_POLINT4F (TV(Nt-3), Fvec(1), tt, ff)
      Else
C                         Interrior bins : (JLq.GE.1 .and. JLq.LE.Nt-2)
C       which include JLq.Eq.1 and JLq.Eq.Nt-2, since Upd is defined for
C                         the full range QV(0:Nt)  (in contrast to XV)
        tf2 = fvec(2)
        tf3 = fvec(3)

        g1 = ( tf2*t13 - tf3*t12) / t23
        g4 = (-tf2*t34 + tf3*t24) / t23

        h00 = ((t34*ty2-tmp2*ty3)*(fvec(1)-g1)/t12
     &    +  (tmp1*ty2-t12*ty3)*(fvec(4)-g4)/t34)

        ff = (h00*ty2*ty3/tdet + tf2*ty3 - tf3*ty2) / t23
      EndIf

      PHO_PARTONX12 = ff

      Return
      End


 
 


CDECK  ID>, PHO_PARTPT
      SUBROUTINE PHO_PARTPT(IMODE,IF,IL,PTCUT,IREJ)
C********************************************************************
C
C    assign to soft partons
C
C    input:  IMODE   -2   output of statistics
C                    -1   initialization
C                     0   sampling of pt for soft partons belonging to
C                         soft Pomerons
C                     1   sampling of pt for soft partons belonging to
C                         hard Pomerons
C            IF           first entry in /POEVT1/ to check
C            IL           last entry in /POEVT1/ to check
C            PTCUT        current value of PTCUT to distinguish
C                         between soft and hard
C
C    output: IREJ     0   success
C                     1   failure
C
C    (soft pt is sampled by call to PHO_SOFTPT)
C
C********************************************************************
      IMPLICIT DOUBLE PRECISION (A-H,O-Z)
      SAVE

      PARAMETER ( DEPS = 1.D-15 )

      INTEGER IMODE,IF,IL,IREJ
      DOUBLE PRECISION PTCUT

C  input/output channels
      INTEGER LI,LO ,LPRI
      COMMON /POINOU/ LI,LO ,LPRI

C  event debugging information
      INTEGER NMAXD
      PARAMETER (NMAXD=100)
      INTEGER IDEB,KSPOM,KHPOM,KSREG,KHDIR,KACCEP,KSTRG,KHTRG,KSLOO,
     &        KHLOO,KSDPO,KHDPO,KEVENT,KSOFT,KHARD
      COMMON /PODEBG/ IDEB(NMAXD),KSPOM,KHPOM,KSREG,KHDIR,KACCEP,KSTRG,
     &                KHTRG,KSLOO,KHLOO,KSDPO,KHDPO,KEVENT,KSOFT,KHARD

C  model switches and parameters
      CHARACTER*8 MDLNA
      INTEGER ISWMDL,IPAMDL
      DOUBLE PRECISION PARMDL
      COMMON /POMDLS/ MDLNA(50),ISWMDL(50),PARMDL(400),IPAMDL(400)

C  some constants
      DOUBLE PRECISION PI,PI2,PI4,GEV2MB,Q_ch,Q_ch2,Q_ch4
      COMMON /POCONS/ PI,PI2,PI4,GEV2MB,
     &  Q_ch(-6:6),Q_ch2(-6:6),Q_ch4(-6:6)

C  data of c.m. system of Pomeron / Reggeon exchange
      INTEGER NPOSP,IDPDG1,IDBAM1,IDPDG2,IDBAM2
      DOUBLE PRECISION ECMP,PCMP,PMASSP,PVIRTP,GAMBEP,
     &                 SIDP,CODP,SIFP,COFP
      COMMON /POPCMS/ ECMP,PCMP,PMASSP(2),PVIRTP(2),GAMBEP(4),
     &                SIDP,CODP,SIFP,COFP,NPOSP(2),
     &                IDPDG1,IDBAM1,IDPDG2,IDBAM2

C  standard particle data interface
      INTEGER NMXHEP
      PARAMETER (NMXHEP=8000)
      INTEGER NEVHEP,NHEP,ISTHEP,IDHEP,JMOHEP,JDAHEP
      DOUBLE PRECISION PHEP,VHEP
      COMMON /POEVT1/ NEVHEP,NHEP,ISTHEP(NMXHEP),IDHEP(NMXHEP),
     &                JMOHEP(2,NMXHEP),JDAHEP(2,NMXHEP),PHEP(5,NMXHEP),
     &                VHEP(4,NMXHEP)

C  extension to standard particle data interface (PHOJET specific)
      INTEGER IMPART,IPHIST,ICOLOR
      COMMON /POEVT2/ IMPART(NMXHEP),IPHIST(2,NMXHEP),ICOLOR(2,NMXHEP)

      DOUBLE PRECISION PTS,PB,XP,XPB,PC
      DIMENSION PTS(0:2,50),PB(0:2,2),XP(50),XPB(2),PC(4)

      INTEGER MODIFY,IV,IVB
      DIMENSION MODIFY(50),IV(50),IVB(2)

C  debug output
      IF (LPRI.GT.4 .AND. IDEB(6).GE.10)
     &WRITE(LO,'(1X,A,3I4,1P,E11.3)')
     &  'PHO_PARTPT: called with IMODE,IF,IL,PTCUT',
     &  IMODE,IF,IL,PTCUT

      IF(IMODE.LT.0) GOTO 1000

      IREJ = 0
      IF((ISWMDL(3).EQ.10).AND.(ISWMDL(4).EQ.10)) RETURN

C  count entries to modify
      IENTRY = 0
      PTCUT2 = PTCUT**2
      EMIN = 1.D20
      IPEAK = 1
      ISTART = IF

C  soft Pomerons

      IF(IMODE.EQ.0) THEN
        DO 300 I=ISTART,IL
          IF((ISTHEP(I).EQ.-1).AND.(ABS(IPHIST(1,I)).LT.100)) THEN
            IENTRY = IENTRY+1
            MODIFY(IENTRY) = I
            XP(IENTRY) = SIGN(PHEP(4,I)/ECMP,PHEP(3,I))
            IV(IENTRY) = 0
            IF(IDHEP(I).NE.21) IV(IENTRY) = ICOLOR(2,I)
            IF(PHEP(4,I).LT.EMIN) THEN
              EMIN = PHEP(4,I)
              IPEAK = IENTRY
            ENDIF
          ENDIF
 300    CONTINUE

C  hard Pomeron associated remnants (IPHIST(1,)=100,200,...)

      ELSE IF(IMODE.EQ.1) THEN

        DO 350 I=ISTART,IL
          IF((ISTHEP(I).EQ.-1).AND.(IPHIST(1,I).GE.100)) THEN
            IF(MOD(IPHIST(1,I),100).EQ.0) THEN
              IENTRY = IENTRY+1
              MODIFY(IENTRY) = I
              XP(IENTRY) = SIGN(PHEP(4,I)/ECMP,PHEP(3,I))
              IF(ISWMDL(24).EQ.0) THEN
                IV(IENTRY) = 0
                IF(IDHEP(I).NE.21) IV(IENTRY) = ICOLOR(2,I)
              ELSE IF(ISWMDL(24).EQ.1) THEN
                IV(IENTRY) = -1
              ELSE
                IV(IENTRY) = 1
              ENDIF
              IF(PHEP(4,I).LT.EMIN) THEN
                EMIN = PHEP(4,I)
                IPEAK = IENTRY
              ENDIF
            ENDIF
          ENDIF
 350    CONTINUE

C  something wrong

      ELSE
        IF (LPRI.GT.4)
     &  WRITE(LO,'(/1X,A,I5)') 'PHO_PARTPT:ERROR: invalid mode',IMODE
        CALL PHO_ABORT
      ENDIF

C  debug output
      IF(IDEB(6).GE.5) THEN
        IF (LPRI.GT.4)
     &  WRITE(LO,'(1X,2A,3I4)') 'PHO_PARTPT: ',
     &    'number of partons, IPEAK,MODE',IENTRY,IPEAK,IMODE
        IF(IDEB(6).GE.20) CALL PHO_PREVNT(0)
      ENDIF

C  nothing to do
      IF(IENTRY.LE.1) RETURN

C  sample pt of soft partons

      IF(ISWMDL(5).LE.1) THEN
        ITER = 0
        IPEAK = DT_RNDM(DUM)*IENTRY+1
        CALL PHO_SWAPI(MODIFY(IPEAK),MODIFY(1))
        CALL PHO_SWAPD(XP(IPEAK),XP(1))
        CALL PHO_SWAPI(IV(IPEAK),IV(1))
 400    CONTINUE
C  energy limited sampling
          PSUMX = 0.D0
          PSUMY = 0.D0
          ITER = ITER+1
          IF(ITER.GE.1000) THEN
            IF(IDEB(6).GE.3) THEN
              IF (LPRI.GT.4)
     &        WRITE(LO,'(1X,A,3I5)')
     &          'PHO_PARTPT: rejection for MODE,ENTRY,ITER',
     &          IMODE,IENTRY,ITER
              IF (LPRI.GT.4)
     &        WRITE(LO,'(8X,A,I5)') 'I  II  IV       XP         EP',
     &          IPEAK
              DO 405 I=1,IENTRY
                II = MODIFY(I)
                IF (LPRI.GT.4)
     &          WRITE(LO,'(5X,3I5,1P,2E13.4)')
     &            I,II,IV(I),XP(I),PHEP(4,II)
 405          CONTINUE
              IF(IDEB(6).GE.5) CALL PHO_PREVNT(0)
            ENDIF
            IREJ = 1
            RETURN
          ENDIF
          DO 410 I=2,IENTRY
            II = MODIFY(I)
            PTMX = MIN(PHEP(4,II),PTCUT)
            XPB(1) = XP(I)
            IVB(1) = IV(I)
            IF(ISWMDL(5).EQ.0) THEN
              CALL PHO_SOFTPT(1,PTCUT,PTMX,XPB,IVB,PB)
            ELSE
              CALL PHO_SOFTPT(1,PTMX,PTMX,XPB,IVB,PB)
            ENDIF
            PTS(0,I) = PB(0,1)
            PTS(1,I) = PB(1,1)
            PTS(2,I) = PB(2,1)
            PSUMX = PSUMX+PB(1,1)
            PSUMY = PSUMY+PB(2,1)
 410      CONTINUE
          PTREM = SQRT(PSUMX**2+PSUMY**2)
        IF(PTREM.GT.MIN(PHEP(4,MODIFY(1)),PTCUT)) GOTO 400
        PTS(1,1) = -PSUMX
        PTS(2,1) = -PSUMY
      ELSE IF((ISWMDL(5).EQ.2)
     &        .OR.((IMODE.EQ.1).AND.(ISWMDL(5).EQ.3))) THEN
C  unlimited sampling
        IPEAK = DT_RNDM(PSUMX)*IENTRY+1
        CALL PHO_SWAPI(MODIFY(IPEAK),MODIFY(1))
        CALL PHO_SWAPD(XP(IPEAK),XP(1))
        CALL PHO_SWAPI(IV(IPEAK),IV(1))
        CALL PHO_SOFTPT(IENTRY,PTCUT,PTCUT,XP,IV,PTS)
      ELSE IF(ISWMDL(5).EQ.3) THEN
C  each string has balanced pt
        DO 500 K=1,IENTRY
          IF(IV(K).LE.-90) GOTO 499
          I1 = MODIFY(K)
          IC1 = -ICOLOR(1,I1)
          DO 510 L=K+1,IENTRY
            IF(ICOLOR(1,MODIFY(L)).EQ.IC1) GOTO 511
 510      CONTINUE
          IF (LPRI.GT.4)
     &    WRITE(LO,'(//1X,A,I5)')
     &      'PHO_PARTPT:ERROR: no color found for (line,color)',I1,-IC1
          CALL PHO_ABORT
 511      CONTINUE
          I2 = MODIFY(L)
          AMSQR = (PHEP(4,I1)+PHEP(4,I2))**2-(PHEP(1,I1)+PHEP(1,I2))**2
     &           -(PHEP(2,I1)+PHEP(2,I2))**2-(PHEP(3,I1)+PHEP(3,I2))**2
          AM   = SQRT(AMSQR)
          PTMX = AM/2.D0
          IVB(1) = MAX(IV(K),IV(L))
          XPB(1) = XP(K)
          CALL PHO_SOFTPT(1,PTCUT,PTMX,XPB,IVB,PB)
          PTS(1,K) = PB(1,1)
          PTS(2,K) = PB(2,1)
          PTS(1,L) = -PB(1,1)
          PTS(2,L) = -PB(2,1)
          GAM    = (PHEP(4,I1)+PHEP(4,I2))/AM
          GAMBEZ = (PHEP(3,I1)+PHEP(3,I2))/AM
          PC(1) = PB(1,1)
          PC(2) = PB(2,1)
          PLONG = SQRT(PTMX**2-PB(0,1)**2+1.D-12)
          PC(3) = SIGN(PLONG,PHEP(3,I1))
          PC(4) = PTMX
          CALL PHO_ALTRA(GAM,0.D0,0.D0,GAMBEZ,PC(1),PC(2),PC(3),PC(4),
     &               PTOT,PHEP(1,I1),PHEP(2,I1),PHEP(3,I1),PHEP(4,I1))
          PC(1) = -PC(1)
          PC(2) = -PC(2)
          PC(3) = -PC(3)
          CALL PHO_ALTRA(GAM,0.D0,0.D0,GAMBEZ,PC(1),PC(2),PC(3),PC(4),
     &               PTOT,PHEP(1,I2),PHEP(2,I2),PHEP(3,I2),PHEP(4,I2))
          IV(K) = IV(K)-100
          IV(L) = IV(L)-100
 499      CONTINUE
 500    CONTINUE
      ELSE
        IF (LPRI.GT.4)
     &  WRITE(LO,'(/1X,A,I4)')
     &    'PHO_PARTPT:ERROR: invalid value of ISWMDL(5):',ISWMDL(5)
        CALL PHO_ABORT
      ENDIF

C  change partons in /POEVT1/
      DO 900 II=1,IENTRY
        IF(IV(II).GT.-90) THEN
          I = MODIFY(II)
          PHEP(1,I) = PHEP(1,I)+PTS(1,II)
          PHEP(2,I) = PHEP(2,I)+PTS(2,II)
          AMSQR = PHEP(4,I)**2
     &             -PHEP(1,I)**2-PHEP(2,I)**2-PHEP(3,I)**2
          PHEP(5,I) = SIGN(SQRT(ABS(AMSQR)),AMSQR)
        ENDIF
 900  CONTINUE

C  debug output
      IF(IDEB(6).GE.15) THEN
        IF (LPRI.GT.4)
     &  WRITE(LO,'(1X,A,/,8X,A,I5)') 'PHO_PARTPT: table of momenta',
     &    'I  II  IV    XP    EP    PTS   PTX   PTY',IPEAK
        DO 505 I=1,IENTRY
          II = MODIFY(I)
          IF (LPRI.GT.4)
     &    WRITE(LO,'(2X,3I5,1P,5E12.4)')
     &      I,II,IV(I),XP(I),PHEP(4,II),PTS(0,I),PTS(1,I),PTS(2,I)
 505    CONTINUE
        CALL PHO_PREVNT(0)
      ENDIF
      RETURN

C  initialization / output of statistics
 1000 CONTINUE
      CALL PHO_SOFTPT(IMODE,PTM,PTM,XP,IV,PTS)

      END


 
 


CDECK  ID>, PHO_PDF
      SUBROUTINE PHO_PDF(NPAR,X,SCALE2,P2VIR,PD)
C***************************************************************
C
C     call different PDF sets for different particle types
C
C     input:      NPAR     1     IGRP(1),ISET(1)
C                          2     IGRP(2),ISET(2)
C                 X        momentum fraction
C                 SCALE2   squared scale (GeV**2)
C                 P2VIR    particle virtuality (positive, GeV**2)
C
C     output      PD(-6:6) field containing the x*PDF fractions
C
C***************************************************************
      IMPLICIT DOUBLE PRECISION (A-H,O-Z)
      SAVE

      DIMENSION PD(-6:6)

C  input/output channels
      INTEGER LI,LO ,LPRI
      COMMON /POINOU/ LI,LO ,LPRI

C  currently activated parton density parametrizations
      CHARACTER*8 PDFNAM
      INTEGER IPARID,IPAVA,ITYPE,IGRP,ISET,IEXT,NPAOLD
      DOUBLE PRECISION PDFLAM,PDFQ2M
      COMMON /POPPDF/ PDFLAM(2),PDFQ2M(2),PDFNAM(2),IPARID(2),
     &                IPAVA(2),ITYPE(2),IGRP(2),ISET(2),IEXT(2),NPAOLD

C  event debugging information
      INTEGER NMAXD
      PARAMETER (NMAXD=100)
      INTEGER IDEB,KSPOM,KHPOM,KSREG,KHDIR,KACCEP,KSTRG,KHTRG,KSLOO,
     &        KHLOO,KSDPO,KHDPO,KEVENT,KSOFT,KHARD
      COMMON /PODEBG/ IDEB(NMAXD),KSPOM,KHPOM,KSREG,KHDIR,KACCEP,KSTRG,
     &                KHTRG,KSLOO,KHLOO,KSDPO,KHDPO,KEVENT,KSOFT,KHARD

C  model switches and parameters
      CHARACTER*8 MDLNA
      INTEGER ISWMDL,IPAMDL
      DOUBLE PRECISION PARMDL
      COMMON /POMDLS/ MDLNA(50),ISWMDL(50),PARMDL(400),IPAMDL(400)

C  general particle data      
      DOUBLE PRECISION xm_list,tau_list,gam_list,
     &  xm_psm2_list,xm_vem2_list,xm_b82_list,xm_b102_list,
     &  xm_bb82_list,xm_bb102_list
      INTEGER          ich3_list,iba3_list,iq_list,
     &                 id_psm_list,id_vem_list,id_b8_list,id_b10_list
      COMMON /POPAR2/ xm_list(300),tau_list(300),gam_list(300),
     &  xm_psm2_list(6,6),xm_vem2_list(6,6),
     &  xm_b82_list(6,6,6),xm_b102_list(6,6,6),
     &  xm_bb82_list(6,6,6,6),xm_bb102_list(6,6,6,6),
     &  ich3_list(300),iba3_list(300),iq_list(3,300),
     &  id_psm_list(6,6),id_vem_list(6,6),
     &  id_b8_list(6,6,6),id_b10_list(6,6,6)

      DIMENSION PARAM(20),VALUE(20)
      CHARACTER*20 PARAM

      INTEGER  IPHO_PDG2ID
      EXTERNAL IPHO_PDG2ID
      REAL XR,P2R,Q2R,F2GM,XPDFGM
      DIMENSION XPDFGM(-6:6)

C  check of kinematic boundaries
      XI = X
      IF(X.GT.1.D0) THEN
        IF(IDEB(37).GE.0) THEN
          IF (LPRI.GT.4)
     &    WRITE(LO,'(/,1X,A,E15.8/)')
     &      'PHO_PDF: x>1 (corrected to x=1)',X
          CALL PHO_PREVNT(-1)
        ENDIF
        XI = 0.99999999999D0
      ELSE IF(X.LE.0.D0) THEN
        IF(IDEB(37).GE.0) THEN
          IF (LPRI.GT.4)
     &    WRITE(LO,'(/,1X,A,E15.8/)') 'PHO_PDF: X <= 0 ',X
          CALL PHO_PREVNT(-1)
        ENDIF
        XI = 0.0001D0
      ENDIF

      DO 100 I=-6,6
        PD(I) = 0.D0
 100  CONTINUE
      IRET = 1

      SCALE = SQRT(SCALE2)  
      
      IF ((NPAR.EQ.1) .OR. (NPAR.EQ.2)) THEN

c  INTERNAL PDFS

        IF (IEXT(NPAR).EQ.0) THEN
          IF (IGRP(NPAR).EQ.2) THEN
c  CTEQ-TEA 14 LO PDFS + LO ALPHA_S
              PD(-5) = XI*PHO_CT14PDF( -5, XI, SCALE) !BBAR
              PD(-4) = XI*PHO_CT14PDF( -4, XI, SCALE) !CBAR
              PD(-3) = XI*PHO_CT14PDF( -3, XI, SCALE) !SBAR
              PD(-2) = XI*PHO_CT14PDF( -1, XI, SCALE) !DBAR
              PD(-1) = XI*PHO_CT14PDF( -2, XI, SCALE) !UBAR
              PD(0) = XI*PHO_CT14PDF( 0, XI, SCALE) !G
              PD(1) = XI*PHO_CT14PDF( 2, XI, SCALE) !D
              PD(2) = XI*PHO_CT14PDF( 1, XI, SCALE) !U
              PD(3) = XI*PHO_CT14PDF( 3, XI, SCALE) !S
              PD(4) = XI*PHO_CT14PDF( 4, XI, SCALE) !C
              PD(5) = XI*PHO_CT14PDF( 5, XI, SCALE) !B

              IRET = 0
          ELSE IF (ITYPE(NPAR).EQ.1) THEN
C  proton PDFs
            IF(IGRP(NPAR).EQ.5) THEN
              IF(ISET(NPAR).EQ.3) THEN
                CALL PHO_DOR92HO(XI,SCALE2,UDV,DV,GL,UDB,SB,CB,BB)
                UV = UDV-DV
                UDB = 2.D0*UDB
                DEL = 0.D0
                IRET = 0
              ELSE IF(ISET(NPAR).EQ.4) THEN
                CALL PHO_DOR92LO(XI,SCALE2,UDV,DV,GL,UDB,SB,CB,BB)
                UV = UDV-DV
                UDB = 2.D0*UDB
                DEL = 0.D0
                IRET = 0
              ELSE IF(ISET(NPAR).EQ.5) THEN
                CALL PHO_DOR94HO(XI,SCALE2,UV,DV,DEL,UDB,SB,GL)
C  heavy quarks from GRV92-HO
                AMU2  = 0.3
                ALAM2 = 0.248 * 0.248
                S  = LOG (LOG(SCALE2/ALAM2) / LOG(AMU2/ALAM2))
                SC  =  0.820
                ALC =   0.98
                BEC =   0.0
                AKC = -0.625 - 0.523 * S
                AGC =   0.0
                BC  =  1.896 + 1.616 * S
                DC  =   4.12 + 0.683 * S
                EC  =   4.36 + 1.328 * S
                ESC =  0.677 + 0.679 * S
                CB  = PHO_DOR92FS(X,S,SC,ALC,BEC,AKC,AGC,BC,DC,EC,ESC)
                SBO =  1.297
                ALB =   0.99
                BEB =   0.0
                AKB =   0.0  - 0.193 * S
                AGB =   0.0
                BBO =   0.0
                DB  =  3.447 + 0.927 * S
                EB  =   4.68 + 1.259 * S
                ESB =  1.892 + 2.199 * S
                BB  = PHO_DOR92FS(X,S,SBO,ALB,BEB,AKB,AGB,BBO,DB,EB,ESB)
                IRET = 0
              ELSE IF(ISET(NPAR).EQ.6) THEN
                CALL PHO_DOR94LO(XI,SCALE2,UV,DV,DEL,UDB,SB,GL)
C  heavy quarks from GRV92-LO
                AMU2  = 0.25
                ALAM2 = 0.232D0**2
                S  = LOG (LOG(SCALE2/ALAM2) / LOG(AMU2/ALAM2))
                SC  =  0.888
                ALC =   1.01
                BEC =   0.37
                AKC =   0.0
                AGC =   0.0
                BC  =   4.24 - 0.804 * S
                DC  =   3.46 + 1.076 * S
                EC  =   4.61 + 1.490 * S
                ESC =  2.555 + 1.961 * S
                CB  = PHO_DOR92FS(X,S,SC,ALC,BEC,AKC,AGC,BC,DC,EC,ESC)
                SBO =  1.351
                ALB =   1.00
                BEB =   0.51
                AKB =   0.0
                AGB =   0.0
                BBO =  1.848
                DB  =  2.929 + 1.396 * S
                EB  =   4.71 + 1.514 * S
                ESB =   4.02 + 1.239 * S
                BB  = PHO_DOR92FS(X,S,SBO,ALB,BEB,AKB,AGB,BBO,DB,EB,ESB)
                IRET = 0
              ELSE IF(ISET(NPAR).EQ.7) THEN
                CALL PHO_DOR94DI(XI,SCALE2, UV, DV, DEL, UDB, SB, GL)
C  heavy quarks from GRV92-HO
                AMU2  = 0.3
                ALAM2 = 0.248 * 0.248
                S  = LOG (LOG(SCALE2/ALAM2) / LOG(AMU2/ALAM2))
                SC  =  0.820
                ALC =   0.98
                BEC =   0.0
                AKC = -0.625 - 0.523 * S
                AGC =   0.0
                BC  =  1.896 + 1.616 * S
                DC  =   4.12 + 0.683 * S
                EC  =   4.36 + 1.328 * S
                ESC =  0.677 + 0.679 * S
                CB  = PHO_DOR92FS(X,S,SC,ALC,BEC,AKC,AGC,BC,DC,EC,ESC)
                SBO =  1.297
                ALB =   0.99
                BEB =   0.0
                AKB =   0.0  - 0.193 * S
                AGB =   0.0
                BBO =   0.0
                DB  =  3.447 + 0.927 * S
                EB  =   4.68 + 1.259 * S
                ESB =  1.892 + 2.199 * S
                BB  = PHO_DOR92FS(X,S,SBO,ALB,BEB,AKB,AGB,BBO,DB,EB,ESB)
                IRET = 0
              ELSE IF(ISET(NPAR).EQ.8) THEN
                CALL PHO_DOR98LO(XI,SCALE2,UV,DV,US,DS,SB,GL)
                DEL = DS-US
                UDB = DS+US
C  heavy quarks from GRV92-LO
                AMU2  = 0.25
                ALAM2 = 0.232D0**2
                S  = LOG (LOG(SCALE2/ALAM2) / LOG(AMU2/ALAM2))
                SC  =  0.888
                ALC =   1.01
                BEC =   0.37
                AKC =   0.0
                AGC =   0.0
                BC  =   4.24 - 0.804 * S
                DC  =   3.46 + 1.076 * S
                EC  =   4.61 + 1.490 * S
                ESC =  2.555 + 1.961 * S
                CB  = PHO_DOR92FS(X,S,SC,ALC,BEC,AKC,AGC,BC,DC,EC,ESC)
                SBO =  1.351
                ALB =   1.00
                BEB =   0.51
                AKB =   0.0
                AGB =   0.0
                BBO =  1.848
                DB  =  2.929 + 1.396 * S
                EB  =   4.71 + 1.514 * S
                ESB =   4.02 + 1.239 * S
                BB  = PHO_DOR92FS(X,S,SBO,ALB,BEB,AKB,AGB,BBO,DB,EB,ESB)
                IRET = 0
              ELSE IF(ISET(NPAR).EQ.9) THEN
*               CALL PHO_DOR98SC(XI,SCALE2,UV,DV,US,DS,SB,GL)
                DEL = DS-US
                UDB = DS+US
C  heavy quarks from GRV92-LO
                AMU2  = 0.25
                ALAM2 = 0.232D0**2
                S  = LOG (LOG(SCALE2/ALAM2) / LOG(AMU2/ALAM2))
                SC  =  0.888
                ALC =   1.01
                BEC =   0.37
                AKC =   0.0
                AGC =   0.0
                BC  =   4.24 - 0.804 * S
                DC  =   3.46 + 1.076 * S
                EC  =   4.61 + 1.490 * S
                ESC =  2.555 + 1.961 * S
                CB  = PHO_DOR92FS(X,S,SC,ALC,BEC,AKC,AGC,BC,DC,EC,ESC)
                SBO =  1.351
                ALB =   1.00
                BEB =   0.51
                AKB =   0.0
                AGB =   0.0
                BBO =  1.848
                DB  =  2.929 + 1.396 * S
                EB  =   4.71 + 1.514 * S
                ESB =   4.02 + 1.239 * S
                BB  = PHO_DOR92FS(X,S,SBO,ALB,BEB,AKB,AGB,BBO,DB,EB,ESB)
                IRET = 0
              ENDIF
              PD(-5) = BB
              PD(-4) = CB
              PD(-3) = SB
              PD(-2) = 0.5D0*(UDB-DEL)
              PD(-1) = 0.5D0*(UDB+DEL)
              PD(0)  = GL
              PD(1)  = DV+PD(-1)
              PD(2)  = UV+PD(-2)
              PD(3)  = PD(-3)
              PD(4)  = PD(-4)
              PD(5)  = PD(-5)
            ENDIF
          ELSE IF(ITYPE(NPAR).EQ.2) THEN
C  pion PDFs (default for pi+)
            IF(IGRP(NPAR).EQ.5) THEN
              IF(ISET(NPAR).EQ.1) THEN
                CALL PHO_DORPHO (XI,SCALE2,VA,GL,QB,CB,BB)
                IRET = 0
              ELSE IF(ISET(NPAR).EQ.2) THEN
                CALL PHO_DORPLO (XI,SCALE2,VA,GL,QB,CB,BB)
                IRET = 0
              ENDIF
              PD(-5) = BB
              PD(-4) = CB
              PD(-3) = QB
              PD(-2) = QB
              PD(-1) = QB+VA
              PD(0)  = GL
              PD(1)  = QB
              PD(2)  = VA+QB
              PD(3)  = QB
              PD(4)  = CB
              PD(5)  = BB
            ENDIF
          ELSE IF(ITYPE(NPAR).EQ.3) THEN
C  photon PDFs
            IF(IGRP(NPAR).EQ.5) THEN
              IF(ISET(NPAR).EQ.1) THEN
                CALL PHO_DORGH0 (XI,SCALE2,UB,DB,SB,CB,BB,GL)
                IRET = 0
              ELSE IF(ISET(NPAR).EQ.2) THEN
                CALL PHO_DORGHO (XI,SCALE2,UB,DB,SB,CB,BB,GL)
                IRET = 0
              ELSE IF(ISET(NPAR).EQ.3) THEN
                CALL PHO_DORGLO (XI,SCALE2,UB,DB,SB,CB,BB,GL)
                IRET = 0
              ENDIF
C  reweight with Drees-Godbole factor
              WGX = 1.D0
              IF(P2VIR.GT.0.001D0) THEN
                WGX = LOG(SCALE2/(P2VIR+PARMDL(144)))
     &               /LOG(SCALE2/PARMDL(144))
                WGX = MAX(WGX,0.D0)
              ENDIF
              PD(-5) = BB*WGX/137.D0
              PD(-4) = CB*WGX/137.D0
              PD(-3) = SB*WGX/137.D0
              PD(-2) = UB*WGX/137.D0
              PD(-1) = DB*WGX/137.D0
              PD(0)  = GL*WGX*WGX/137.D0
              PD(1)  = PD(-1)
              PD(2)  = PD(-2)
              PD(3)  = PD(-3)
              PD(4)  = PD(-4)
              PD(5)  = PD(-5)
            ELSE IF(IGRP(NPAR).EQ.8) THEN
              IF(ISET(NPAR).EQ.1) THEN
                CALL PHO_PHGAL (XI,SCALE2,PD)
                IRET = 0
              ENDIF
            ENDIF
          ELSE IF(ITYPE(NPAR).EQ.20) THEN
C  Pomeron PDFs
            MODE = IGRP(NPAR)
            IF(MODE.EQ.1) THEN
              PD(0) = 6.D0*(1.D0-XI)**5*PARMDL(26)*PARMDL(78)
              IRET = 0
            ELSE IF(MODE.EQ.2) THEN
              PD(0) = 6.D0*XI*(1.D0-XI)*PARMDL(26)*PARMDL(78)
              IRET = 0
            ELSE IF(MODE.EQ.3) THEN
              PD(0) = (0.18D0/XI+5.46D0)*(1.D0-XI)*PARMDL(26)*PARMDL(78)
              IRET = 0
            ELSE IF(MODE.EQ.4) THEN
              CALL PHO_CKMTPD(990,XI,SCALE2,PD)
              DO 105 I=-4,4
                PD(I) = PD(I)*PARMDL(78)
 105          CONTINUE
              IRET = 0
            ENDIF
          ENDIF

C  external PDFs

        ELSE IF(IEXT(NPAR).EQ.2) THEN
C  PDFLIB call: new PDF numbering
          IF(NPAR.NE.NPAOLD) THEN
            PARAM(1) = 'NPTYPE'
            PARAM(2) = 'NGROUP'
            PARAM(3) = 'NSET'
            PARAM(4) = ' '
            VALUE(1) = ITYPE(NPAR)
            VALUE(2) = ABS(IGRP(NPAR))
            VALUE(3) = ISET(NPAR)
            CALL PDFSET(PARAM,VALUE)
          ENDIF
          IF(ITYPE(NPAR).EQ.3) THEN
            IP2 = 0
            CALL STRUCTP(XI,SCALE2,P2VIR,IP2,PD(2),PD(1),PD(-2),PD(-1),
     &                   PD(-3),PD(-4),PD(-5),PD(-6),PD(0))
          ELSE
            SCALE = SQRT(SCALE2)
            CALL STRUCTM(XI,SCALE,PD(2),PD(1),PD(-2),PD(-1),
     &                   PD(-3),PD(-4),PD(-5),PD(-6),PD(0))
          ENDIF
          DO 115 I=3,6
            PD(I) = PD(-I)
 115      CONTINUE
          IF(ITYPE(NPAR).EQ.1) THEN
C  proton valence quarks
            PD(1) = PD(1)+PD(-1)
            PD(2) = PD(2)+PD(-2)
          ELSE IF(ITYPE(NPAR).EQ.2) THEN
C  pi+ valences
            DVAL = PD(1)
            PD(1) = PD(-1)
            PD(-1) = DVAL+PD(1)
            PD(2) = PD(2)+PD(-2)
          ELSE IF(ITYPE(NPAR).EQ.3) THEN
C  photon conventions
            PD(1) = PD(-1)
            PD(2) = PD(-2)
          ENDIF
          IRET = 0

        ELSE IF(IEXT(NPAR).EQ.3) THEN
C  PHOLIB call: version 2.0
          CALL PHVAL(IGRP(NPAR),ISET(NPAR),XI,SCALE2,PD,IRET)
          IF(IRET.LT.0) THEN
            IF (LPRI.GT.4)
     &      WRITE(LO,'(/1X,A,I2)')
     &        'PHO_PDF:ERROR: non-vanishing PHVAL return code',IRET
            CALL PHO_ABORT
          ENDIF
          IRET = 0

C  photon PDFs depending on photon virtuality

        ELSE IF(IEXT(NPAR).EQ.4) THEN
          IF(IGRP(NPAR).EQ.1) THEN
C  Schuler/Sjostrand PDF (interface to single precision)
            XR = SNGL(XI)
            Q2R = SNGL(SCALE2)
            P2R = SNGL(P2VIR)
            IP2 = 0
            CALL PHO_SASGAM(ISET(NPAR),XR,Q2R,P2R,IP2,F2GM,XPDFGM)
            DO 120 I=-6,6
              PD(I) = DBLE(XPDFGM(I))
 120        CONTINUE
            IRET = 0
          ELSE IF(IGRP(NPAR).EQ.5) THEN
C  Gluck/Reya/Stratmann
            IF(ISET(NPAR).EQ.4) THEN
              CALL PHO_DORGLV (XI,SCALE2,P2VIR, UB, DB, SB, GL)
              CALL PHO_QPMPDF(4,XI,SCALE2,0.D0,P2VIR,CB)
              IRET = 0
              PD(-5) = 0.D0
              PD(-4) = CB
              PD(-3) = SB/137.D0
              PD(-2) = UB/137.D0
              PD(-1) = DB/137.D0
              PD(0)  = GL/137.D0
              PD(1)  = PD(-1)
              PD(2)  = PD(-2)
              PD(3)  = PD(-3)
              PD(4)  = PD(-4)
              PD(5)  = PD(-5)
            ENDIF
          ENDIF
        ENDIF

C  check for errors

        IF(IRET.NE.0) THEN
          IF (LPRI.GT.4)
     &    WRITE(LO,'(/1X,A,/10X,5I6)')
     &      'PHO_PDF:ERROR:unsupported PDF(NPAR,IEXT,ITYPE,IGRP,ISET)',
     &      NPAR,IEXT(NPAR),ITYPE(NPAR),IGRP(NPAR),ISET(NPAR)
          CALL PHO_ABORT
        ENDIF
C  error in NPAR
      ELSE
        IF (LPRI.GT.4)
     &  WRITE(LO,'(/1X,A,I5)') 'PHO_PDF:ERROR:invalid NPAR(1,2) ',NPAR
        CALL PHO_ABORT
      ENDIF
      NPAOLD = NPAR

C  valence quark treatment
      ipabs = abs(iparid(npar))
      ipsign = sign(1,iparid(npar))
      If ((itype(npar).EQ.2) .And. (ipabs.NE.211)) Then
C  valence quarks for mesons but pi+
        idcpc = IPHO_PDG2ID(ipabs)
        If (iq_list(3, idcpc) .NE. 0) Then
          IF (LPRI.GT.4)
     .    Write(lo,*) 'PHO_PDF: Error determining meson valences.'
          CALL PHO_ABORT
        End If

C  Ordinary meson with fixed valence quarks
        If (iq_list(1, idcpc) .NE. iq_list(2, idcpc)) Then
C  down-flavor valence
          dfval = pd(-1) - pd(1)
C  up-flavor valence 
          ufval = pd(2) - pd(-2)
C   set valence density to sea density
          pd(-1) = pd(1)
          pd(2) = pd(-2)
          Do iv=1,2
C    Valence is up-type (u, c, t)
            If (mod(iq_list(iv, idcpc),2) .EQ. 0) Then
              pd(iq_list(iv, idcpc)) = pd(iq_list(iv, idcpc)) + ufval
            Else
C    Valence is down-type (d,s,b)
              pd(iq_list(iv, idcpc)) = pd(iq_list(iv, idcpc)) + dfval
            End If
          End Do

C    neutral kaon mixed state
        Else If ((ipabs.EQ.310) .Or. (ipabs.EQ.130)) Then
          vals = pd(-1) - pd(1)
          valu = pd(2) - pd(-2)
          pd(-1) = pd(1)
          pd(2) = pd(-2)
          pd(2) = pd(2) + valu/2.D0
          pd(-2) = pd(-2) + valu/2.D0
          pd(3) = pd(3) + vals/2.D0
          pd(-3) = pd(-3) + vals/2.D0
C   unflavored meson: pi0, eta,...
        Else
          ifmax = max(2,abs(iq_list(1, idcpc)))
          dfval = pd(-1) - pd(1)
          ufval = pd(2) - pd(-2)
          avval = (dfval + ufval)/(2.D0*dble(ifmax))
C    set valence density to sea density
          pd(-1) = pd(1)
          pd(2) = pd(-2)
C    Add average valence density to all contributing flavors
          Do ifl = 1,ifmax
            pd(ifl) = pd(ifl) + avval
            pd(-ifl) = pd(-ifl) + avval
          End Do

        End If

      Else If ((itype(npar).EQ.1) .And. (ipabs.NE.2212)) Then
C  valence quarks for nucleons but proton
        If (ipabs.EQ.2112) Then
C  neutron
          CALL PHO_SWAPFLAV(pd, 1, 2)
        Else 
          idcpc = IPHO_PDG2ID(ipabs)
          iq_list(1, idcpc) = iq_list(1, idcpc)
          iq_list(2, idcpc) = iq_list(2, idcpc)
          ival3 = iq_list(3, idcpc)
          If (iq_list(3, idcpc) .EQ. 0) Then
            IF (LPRI.GT.4) 
     &      Write(LO,*) 'PHO_PDF: Error determining baryon valences.'
            Write(6,*) ipabs, idcpc, (iq_list(i,idcpc),i=1,3)
            CALL PHO_ABORT
          End If

C   Ordinary baryon
C   down-flavor valence
          dfval = pd(1) - pd(-1)
C   up-flavor valence 
          ufval = (pd(2) - pd(-2))/2.D0
C    set valence density to sea density
          pd(1) = pd(-1)
          pd(2) = pd(-2)

          Do iv = 1,3
            If (mod(iq_list(iv, idcpc),2) .EQ. 0) Then
C    Valence 1 is up-type (u, c, t)
              pd(iq_list(iv, idcpc)) = pd(iq_list(iv, idcpc)) + ufval
            Else
C    Valence 1 is down-type (d,s,b)
              pd(iq_list(iv, idcpc)) = pd(iq_list(iv, idcpc)) + dfval
            End If
          End Do
        End If
      End If

C  antiparticle
      IF(IPARID(NPAR).LT.0) THEN
        DO 190 I=1,4
          DUM=PD(I)
          PD(I)=PD(-I)
          PD(-I)=DUM
 190    CONTINUE
      ENDIF
      
C  optionally remove valence quarks
      IF(IPAVA(NPAR).EQ.0) THEN
        DO 200 I=1,4
          PD(I) = MIN(PD(-I),PD(I))
          PD(-I) = PD(I)
 200    CONTINUE
      ENDIF


C  debug information
      IF (LPRI.GT.4 .AND. IDEB(37).GE.30)
     &WRITE(LO,
     &  '(1X,A,I4,1P,3E12.4/,2X,A,6E10.3,/2X,A,E10.3,/2X,A,6E10.3)')
     &  'PHO_PDF: NPAR,X,SCALE**2,P2VIR',
     &  NPAR,X,SCALE2,P2VIR,'PD(-6..-1)',(PD(I),I=-6,-1),
     &  'PD(0)     ',PD(0),'PD(1..6)  ',(PD(I),I=1,6)

      END


 
 


CDECK  ID>, PHO_PDFTST
      SUBROUTINE PHO_PDFTST(IDPDG,SCALE2,P2MASS)
C*********************************************************************
C
C     structure function test utility
C
C     input:    IDPDG    PDG ID of particle
C               SCALE2   squared scale (GeV**2)
C               P2MASS   particle virtuality (pos, GeV**2)
C
C     output:   tables of PDF, sum rule checking, table of F2
C
C*********************************************************************
      IMPLICIT DOUBLE PRECISION (A-H,O-Z)
      SAVE

C  input/output channels
      INTEGER LI,LO ,LPRI
      COMMON /POINOU/ LI,LO ,LPRI

C  currently activated parton density parametrizations
      CHARACTER*8 PDFNAM
      INTEGER IPARID,IPAVA,ITYPE,IGRP,ISET,IEXT,NPAOLD
      DOUBLE PRECISION PDFLAM,PDFQ2M
      COMMON /POPPDF/ PDFLAM(2),PDFQ2M(2),PDFNAM(2),IPARID(2),
     &                IPAVA(2),ITYPE(2),IGRP(2),ISET(2),IEXT(2),NPAOLD

C  some constants
      DOUBLE PRECISION PI,PI2,PI4,GEV2MB,Q_ch,Q_ch2,Q_ch4
      COMMON /POCONS/ PI,PI2,PI4,GEV2MB,
     &  Q_ch(-6:6),Q_ch2(-6:6),Q_ch4(-6:6)


      DIMENSION PD(-6:6),PDSUM(-6:6),PDAVE(-6:6),FXP(4)
      CHARACTER*8 PDFNA

      CALL PHO_ACTPDF(IDPDG,1)
      CALL PHO_GETPDF(1,PDFNA,ALA,Q2MI,Q2MA,XMI,XMA)

      IF (LPRI.GT.4)
     &WRITE(LO,'(/,A)') ' *** Structure Function Test Utility ***'
      IF (LPRI.GT.4)
     &WRITE(LO,'(A)') ' ======================================='

      IF (LPRI.GT.4)
     &WRITE(LO,'(/,A,3I10)')
     &  ' used structure function:',ITYPE(1),IGRP(1),ISET(1)
      IF (LPRI.GT.4)
     &WRITE(LO,'(A,A)')     ' corresponds to ',PDFNA
      IF (LPRI.GT.4)
     &WRITE(LO,'(A,E12.3)') '  used squared scale (GeV**2):',SCALE2
      IF (LPRI.GT.4)
     &WRITE(LO,'(A,E12.3)') ' particle virtuality (GeV**2):',P2MASS
      IF (LPRI.GT.4)
     &WRITE(LO,'(/1X,A)') 'x times parton densities'
      IF (LPRI.GT.4)
     &WRITE(LO,'(1X,A)') '    X         PD(-4 - 4)'
      IF (LPRI.GT.4)
     &WRITE(LO,'(1X,A)')
     &   ' ============================================================'

C  logarithmic loop over x values
C  upper bound
      XUPPER=0.9999D0
C  lower bound
      XLOWER=1.D-4
C  number of steps
      NSTEP=50

      XFIRST=LOG(XLOWER)
      XDELTA=LOG(XUPPER/XLOWER)/DBLE(NSTEP-1)
      DO 100 I=1,NSTEP
        X=EXP(XFIRST)
        XCONTR=X
        CALL PHO_PDF(1,X,SCALE2,P2MASS,PD)
        IF(X.NE.XCONTR) THEN
          IF (LPRI.GT.4)
     &    WRITE(LO,*) ' PHO_PDFTST: x changed! old: ',XCONTR,' new: ',X
        ENDIF
        IF (LPRI.GT.4)
     &  WRITE(LO,'(1X,1P,10E11.4)') XCONTR,(PD(K),K=-4,4)
        XFIRST=XFIRST+XDELTA
 100  CONTINUE

      IF(IDPDG.EQ.22) THEN
        IF (LPRI.GT.4)
     &  WRITE(LO,'(/1X,A)')
     &   'comparison PDF to contribution due to box diagram'
        IF (LPRI.GT.4)
     &  WRITE(LO,'(1X,A)') '    X   PD(1),PB(1), .... ,PD(4),PB(4)'
        IF (LPRI.GT.4)
     &  WRITE(LO,'(1X,A)')
     &   ' ============================================================'
        XFIRST=LOG(XLOWER)
        XDELTA=LOG(XUPPER/XLOWER)/DBLE(NSTEP-1)
        DO 110 I=1,NSTEP
          X=EXP(XFIRST)
          CALL PHO_PDF(1,X,SCALE2,P2MASS,PD)
          DO 120 K=1,4
            CALL PHO_QPMPDF(K,X,SCALE2,0.D0,P2MASS,FXP(K))
 120      CONTINUE
          IF (LPRI.GT.4)
     &    WRITE(LO,'(1X,1P,9E11.4)') X,(PD(K),FXP(K),K=1,4)
          XFIRST=XFIRST+XDELTA
 110    CONTINUE
      ENDIF

C  check momentum sum rule

      IF (LPRI.GT.4)
     &WRITE(LO,'(/1X,A)') 'PHO_PDFTST: estimate of quark sum rules'
      DO 199 I=-6,6
        PDSUM(I) = 0.D0
        PDAVE(I) = 0.D0
 199  CONTINUE
      ITER=5000
      DO 200 I=1,ITER
        XX=DBLE(I)/DBLE(ITER)
        IF(XX.EQ.1.D0) XX = 0.999999D0
        CALL PHO_PDF(1,XX,SCALE2,P2MASS,PD)
        DO 202 K=-6,6
          PDSUM(K) = PDSUM(K)+PD(K)/XX
          PDAVE(K) = PDAVE(K)+PD(K)
 202    CONTINUE
 200  CONTINUE
      IF (LPRI.GT.4)
     &WRITE(LO,'(1X,A)')
     &  'Table: parton-ID, dx-integral over Q(X,Q**2), X*Q(X,Q**2)'
      XSUM = 0.D0
      DO 204 I=-6,6
        PDSUM(I) = PDSUM(I)/DBLE(ITER)
        PDAVE(I) = PDAVE(I)/DBLE(ITER)
        XSUM = XSUM+PDAVE(I)
        IF (LPRI.GT.4)
     &  WRITE(LO,'(9X,I3,3X,2E15.4)') I,PDSUM(I),PDAVE(I)
 204  CONTINUE
      IF (LPRI.GT.4)
     &WRITE(LO,'(1X,A)') 'PHO_PDFTST: valence flavours'
      DO 205 I=1,6
        IF (LPRI.GT.4)
     &  WRITE(LO,'(9X,I3,E12.4)') I,PDSUM(I)-PDSUM(-I)
 205  CONTINUE
      IF (LPRI.GT.4)
     &WRITE(LO,'(1X,A,E12.4)') 'momentum sum rule',XSUM
      IF (LPRI.GT.4)
     &WRITE(LO,'(A/)') ' ============================================='

C  table of F2

      IF (LPRI.GT.4)
     &WRITE(LO,'(/1X,A,E12.4,/1X,A)')
     &  'PHO_PDFTST: TABLE OF X, F2(X,Q**2) FOR Q**2',SCALE2,
     &  '-----------------------------------------------------'
      ITER=100
      DO 300 I=1,ITER
        XX=DBLE(I)/DBLE(ITER)
        IF(XX.EQ.1.D0) XX = 0.9999D0
        CALL PHO_PDF(1,XX,SCALE2,P2MASS,PD)
        F2 = 0.D0
        DO 302 K=-6,6
          IF(K.NE.0) F2 = F2 + Q_ch2(K)*PD(K)
 302    CONTINUE
        IF (LPRI.GT.4)
     &  WRITE(LO,'(5X,1P,2E14.5)') XX,F2
 300  CONTINUE
      IF (LPRI.GT.4)
     &WRITE(LO,'(A/)') ' ============================================='

      END

 
 


CDECK  ID>, PHO_PECMS
      SUBROUTINE PHO_PECMS(ID,PMASS1,PMASS2,ECM,PP,EE)
C*******************************************************************
C
C     calculation of cms momentum and energy of massive particle
C     (ID=  1 using PMASS1,  2 using PMASS2)
C
C     output:  PP    cms momentum
C              EE    energy in CMS of particle ID
C
C********************************************************************
      IMPLICIT DOUBLE PRECISION (A-H,O-Z)
      SAVE

C  input/output channels
      INTEGER LI,LO ,LPRI
      COMMON /POINOU/ LI,LO ,LPRI

C  event debugging information
      INTEGER NMAXD
      PARAMETER (NMAXD=100)
      INTEGER IDEB,KSPOM,KHPOM,KSREG,KHDIR,KACCEP,KSTRG,KHTRG,KSLOO,
     &        KHLOO,KSDPO,KHDPO,KEVENT,KSOFT,KHARD
      COMMON /PODEBG/ IDEB(NMAXD),KSPOM,KHPOM,KSREG,KHDIR,KACCEP,KSTRG,
     &                KHTRG,KSLOO,KHLOO,KSDPO,KHDPO,KEVENT,KSOFT,KHARD

C  some constants
      DOUBLE PRECISION PI,PI2,PI4,GEV2MB,Q_ch,Q_ch2,Q_ch4
      COMMON /POCONS/ PI,PI2,PI4,GEV2MB,
     &  Q_ch(-6:6),Q_ch2(-6:6),Q_ch4(-6:6)


      S=ECM**2
      PM1 = SIGN(PMASS1**2,PMASS1)
      PM2 = SIGN(PMASS2**2,PMASS2)
      PP = SQRT(S**2 - 2.D0*PM1*S - 2.D0*PM2*S - 2.D0*PM1*PM2
     &          + PM1**2 + PM2**2)/(2.D0*ECM)

      IF(ID.EQ.1) THEN
        EE = SQRT( PM1 + PP**2 )
      ELSE IF(ID.EQ.2) THEN
        EE = SQRT( PM2 + PP**2 )
      ELSE
        IF (LPRI.GT.4)
     &  WRITE(LO,'(/1X,A,I3,/)')
     &    'PHO_PECMS:ERROR: invalid ID number:',ID
        EE = PP
      ENDIF

      END


 
 


