*
* Special settings if no Dpmjet input follows the Fluka input
*
      IF (LESDPM .EQ. 0) THEN
        NINP = -1
      ELSE
        NINP = 0
      END IF

*
* Initialization of Dpmjet
*

      IGLAUB= 0

      CALL DT_INIT(NINP,EPN,NPMASS,NPCHAR,NTMASS,NTCHAR,IDP,IGLAU)
      CALL DT_STATIS(1)

c     CALL DT_DTUINI(NINP,EPN,NPMASS,NPCHAR,NTMASS,NTCHAR,IDP,IEMU)
      NEVENT = 0
*



      IF (LPRI .LT. 3) RETURN
*
* Initialization of histograms
*
      IDPMEV = 0
*  mass number of projectile and target nuclei
      XLIM1 = 0.5D0
      XLIM2 = 200.5D0
      XLIM3 = 0d0
      IBIN  = INT(XLIM2-XLIM1)
      CALL DT_NEWHGR(XLIM1,XLIM2,XLIM3,XDUMB,IBIN,IHMAPR)
      CALL DT_NEWHGR(XLIM1,XLIM2,XLIM3,XDUMB,IBIN,IHMATA)
*  index of projectile hadrons
      CALL DT_NEWHGR(XLIM1,XLIM2,XLIM3,XDUMB,IBIN,IHIJPR)
*  energy of projectile nuclei and hadrons
      XLIM1 = 5.0D0
      XLIM2 = PBEAM
