      XLIM3 = 0.D0
      IBIN  = -200
      CALL DT_NEWHGR(XLIM1,XLIM2,XLIM3,XDUMB,IBIN,IHENUC)
      CALL DT_NEWHGR(XLIM1,XLIM2,XLIM3,XDUMB,IBIN,IHEHAD)
*

      RETURN
*=== End of subroutine Dpmini =========================================*
      END


*
*=== Dpmout ===========================================================*
*
      SUBROUTINE DPMOUT(IEVE)

*----------------------------------------------------------------------*
*     Version March 2004                                               *
*     Last change                                                      *
*                                                                      *
*     This subroutine is part of the FLUKA interface to DPMJET 3.      *
*     Printout of DPMJET 3 event generation statistics.                *
*                                                                      *
*----------------------------------------------------------------------*

* histogram indices for Fluka-interface related statistics
      CHARACTER*72 HEADER
      COMMON /DTFLHX/ IHMAPR,IHMATA,IHIJPR,IHENUC,IHEHAD,IDPMEV

      COMMON /DTFLKA/ LINP,LOUT,LDAT, LPRI

      COMMON /DTSTA1/ ICREQU,ICSAMP,ICCPRO,ICDPR,ICDTA,
     &                ICRJSS,ICVV2S,ICCHAI(2,9),ICRES(9),ICDIFF(5),
     &                ICEVTG(8,0:30)
      SAVE


      IF (LPRI .LT. 3 .OR. ICREQU .LT. 1) RETURN

      IF (IEVE.EQ.5) CALL DT_DTUOUT
*
* Write histograms with interaction parameter distributions
*
      CALL DT_EVTHIS(IDUM)
      WRITE(LOUT,*) ' DPMOUT: Normalization factor = ',IDPMEV
      HEADER = ' A_projectile'
      CALL DT_OUTHGR(IHMAPR,0,0,0,0,0,HEADER,0,IDPMEV,1D+00,0,0,-1)
      HEADER = ' A_target'
      CALL DT_OUTHGR(IHMATA,0,0,0,0,0,HEADER,0,IDPMEV,1D+00,0,0,-1)
      HEADER = ' Id_projectile'
      CALL DT_OUTHGR(IHIJPR,0,0,0,0,0,HEADER,0,IDPMEV,1D+00,0,0,-1)
      HEADER = ' dN/dE (nuclei)'
      CALL DT_OUTHGR(IHENUC,0,0,0,0,0,HEADER,0,IDPMEV,1D+00,0,1,-1)
      HEADER = ' dN/dE (hadrons)'
      CALL DT_OUTHGR(IHEHAD,0,0,0,0,0,HEADER,0,IDPMEV,1D+00,0,1,-1)
*
      RETURN
*=== Dpmout ===========================================================*
      END


*
