*
*===densit=============================================================*
*
CDECK  ID>, DT_DENSIT
      DOUBLE PRECISION FUNCTION DT_DENSIT(NA,R,RA)

      IMPLICIT DOUBLE PRECISION (A-H,O-Z)
      SAVE

      COMMON /DTFLKA/ LINP,LOUT,LDAT, LPRI

      PARAMETER (ZERO=0.0D0,TINY10=1.0D-10,ONE=1.0D0,TWO=2.0D0)
      PARAMETER (TWOPI = 6.283185307179586476925286766559D+00,
     &           PI    = TWOPI/TWO)

      DIMENSION R0(18),FNORM(18)
      DATA R0 /  ZERO,   ZERO,   ZERO,   ZERO, 2.12D0,
     &         2.56D0, 2.41D0, 2.46D0, 2.52D0, 2.45D0,
     &         2.37D0, 2.46D0, 2.44D0, 2.54D0, 2.58D0,
     &         2.72D0, 2.66D0, 2.79D0/
      DATA FNORM /.1000D+01,.1000D+01,.1000D+01,.1000D+01,.1000D+01,
     &            .1000D+01,.1000D+01,.1000D+01,.1000D+01,.1000D+01,
     &            .1012D+01,.1039D+01,.1075D+01,.1118D+01,.1164D+01,
     &            .1214D+01,.1265D+01,.1318D+01/
      DATA PDIF /0.545D0/

      DT_DENSIT = ZERO
* shell model
      IF (NA.LE.4) THEN
         STOP 'DT_DENSIT-0'
      ELSEIF ((NA.GT.4).AND.(NA.LE.18)) THEN
         R1 = R0(NA)/SQRT(2.5D0-4.0D0/DBLE(NA))
         DT_DENSIT = (ONE+(DBLE(NA)-4.0D0)/6.0D0*(R/R1)**2)
     &            *EXP(-(R/R1)**2)/FNORM(NA)
* Woods-Saxon
      ELSEIF (NA.GT.18) THEN
         DT_DENSIT = ONE/(ONE+EXP((R-RA)/PDIF))
      ENDIF

      RETURN
      END


*
*===density============================================================*
*
      SUBROUTINE DT_DENSITY
      IMPLICIT DOUBLE PRECISION (A-H,O-Z)
      SAVE

* event history
      PARAMETER (NMXHKK=250000)
      COMMON /DTEVT1/ NHKK,NEVHKK,ISTHKK(NMXHKK),IDHKK(NMXHKK),
     &                JMOHKK(2,NMXHKK),JDAHKK(2,NMXHKK),
     &                PHKK(5,NMXHKK),VHKK(4,NMXHKK),WHKK(4,NMXHKK)

* extended event history
      COMMON /DTEVT2/ IDRES(NMXHKK),IDXRES(NMXHKK),NOBAM(NMXHKK),
     &                IDBAM(NMXHKK),IDCH(NMXHKK),NPOINT(10),
     &                IHIST(2,NMXHKK)

      COMMON /DTFLKA/ LINP,LOUT,LDAT, LPRI

* Lorentz-parameters of the current interaction
      COMMON /DTLTRA/ GACMS(2),BGCMS(2),GALAB,BGLAB,BLAB,
     &                UMO,PPCM,EPROJ,PPROJ

* flags
      LOGICAL LEMCCK,LHADRO,LSEADI,LEVAPO
      COMMON /DTFLG1/ IFRAG(2),IRESCO,IMSHL,IRESRJ,IOULEV(6),
     &                LEMCCK,LHADRO(0:9),LSEADI,LEVAPO,IFRAME,ITRSPT,
     &                IFUSION,IFLOW
C
      DIMENSION ICHAIM(30,30),KCHAIM(30,30,600)
      COMMON /ZAZAKE/ ZAKEIN(100)
C---------------------
*
      DATA NCOUNT/0/


*.     AABBFUS=0.81D0*(1.D0-5.D0/SQRT(UMO))
      AABBFUS=0.45D0
C       parameters im dpm3304
      IF(UMO.GT.100.D0)AABBFUS=0.45D0
      IF(UMO.LT.20.D0)AABBFUS=0.0D0
      IF(UMO.GT.20.D0.AND.UMO.LT.100.D0)AABBFUS=0.45D0*(UMO-20.D0)/80.D0
      IF(LPRI.GT.4) THEN
         IF(NCOUNT.LE.5)WRITE(6,*)" UMO,AABBFUS =",UMO,AABBFUS
      END IF
C        paarameters in dpm3influka
      IF(UMO.GT.100.D0)AABBFUS=0.53D0
      IF(UMO.LT.20.D0)AABBFUS=0.0D0
      IF(UMO.GT.20.D0.AND.UMO.LT.100.D0)AABBFUS=0.53D0*(UMO-20.D0)/80.D0
      IF(LPRI.GT.4) THEN
         IF(NCOUNT.LE.5)WRITE(6,*)" UMO,AABBFUS =",UMO,AABBFUS
      END IF
      NCHAIN=0
      NCHAINN=0
      NCHAIN31=0
      NCHAIN32=0
      NCHAIN33=0
      NCHAIN34=0
      NCHAIN21=0
      NCHAIN22=0
      NCOUNT=NCOUNT+1
      DAAA=0.9D0
      DAAA=1.D0
      DAAA=1.2D0
      DAAA=1.5D0
      DAAA=1.8D0

C     First round of fusions

C     WRITE(6,*)' entry density'
      DO 7776 IIX=1,30
         DO 7775 IIY=1,30
            ICHAIM(IIX,IIY)=0
            DO 7774 KKI=1,600
               KCHAIM(IIX,IIY,KKI)= 0
 7774       CONTINUE
 7775    CONTINUE
 7776 CONTINUE
      DO 4477 JJKEIN=1,100
         ZAKEIN(JJKEIN)=0.D0
 4477 CONTINUE
C
      DO 7667 KK=1,NHKK
         IDH=IDHKK(KK)/10000
         IF(IDH.LT.7)GO TO 7667
         IF(IDH.GT.8)GO TO 7667
C           WRITE(6,'(2I4,I6,4I4,5F10.2,2I3,I2,I4,4E12.3)')
C    *         KK,ISTHKK(KK),IDHKK(KK),
C    *         JMOHKK(1,KK),JMOHKK(2,KK),JDAHKK(1,KK),JDAHKK(2,KK), 
C    *    (PHKK(LL,KK),LL=1,5),IDRES(KK),IDXRES(KK),NOBAM(KK),IDBAM(KK),
C    *    (VHKK(JJ,KK),JJ=1,4)
         RRKEIN=1.D12*SQRT(VHKK(1,KK)**2+VHKK(2,KK)**2)
         IIKEIN=RRKEIN/0.2D0 +2.D0
         DO 4567 JJKEIN=IIKEIN,100
            ZAKEIN(JJKEIN)=ZAKEIN(JJKEIN)+1
 4567    CONTINUE
 7667 CONTINUE
C
C
      DO 7777 KK=1,NHKK
         IDH=IDHKK(KK)/10000
         IF(IDH.LT.7)GO TO 7777
         IF(IDH.GT.8)GO TO 7777
C          WRITE(6,'(2I4,I6,4I4,5F10.2,2I3,I2,I4,4E12.3)')
C    *     KK,ISTHKK(KK),IDHKK(KK),
C    *     JMOHKK(1,KK),JMOHKK(2,KK),JDAHKK(1,KK),JDAHKK(2,KK), 
C    *    (PHKK(LL,KK),LL=1,5),IDRES(KK),IDXRES(KK),NOBAM(KK),IDBAM(KK),
C    *    (VHKK(JJ,KK),JJ=1,4)
         IIX=15.D0+1.D12*VHKK(1,KK)/DAAA
         IIY=15.D0+1.D12*VHKK(2,KK)/DAAA
         IF(IIX.LT.2)IIX=2
         IF(IIY.LT.2)IIY=2
         IF(IIX.GT.29)IIX=29
         IF(IIY.GT.29)IIY=29
         ICHAIM(IIX,IIY)=ICHAIM(IIX,IIY)+1
         NCHAIN=NCHAIN+1
         KKK=ICHAIM(IIX,IIY)
         KCHAIM(IIX,IIY,KKK)=KK
 7777 CONTINUE
C
      IF(NCOUNT.LE.3)THEN
         IF (LPRI.GT.4) THEN
            WRITE(6,*)' JJJ '
            DO 7778 IIY=1,30
               WRITE(6,'(30I3)')(ICHAIM(IIX,IIY),IIX=1,30)
 7778       CONTINUE
            WRITE(6,*)' ZAKEIN '
            DO 3477 JJKEIN=1,100
               WRITE(6,*)JJKEIN,ZAKEIN(JJKEIN)
 3477       CONTINUE
         END IF
      END IF

      IF(IFLOW.EQ.1)THEN
C        WRITE(6,*)' and now KETWW '
C        CALL KETWW(ZAKEIN)
      END IF

C     First round of fusions

      DO 5667 IIX=8,22
         DO 5668 IIY=8,22
            IF(ICHAIM(IIX,IIY).LE.1)GO TO 5668
            DO 6556 KKK=1,ICHAIM(IIX,IIY)-1
               DO 6557 LLL=KKK+1,ICHAIM(IIX,IIY)
                  KK=IDHKK(KCHAIM(IIX,IIY,KKK))/10000
                  IF(KK.LT.7)GO TO 6556
