     &                IHIST(2,NMXHKK)

* flags for input different options
      LOGICAL LEMCCK,LHADRO,LSEADI,LEVAPO
      COMMON /DTFLG1/ IFRAG(2),IRESCO,IMSHL,IRESRJ,IOULEV(6),
     &                LEMCCK,LHADRO(0:9),LSEADI,LEVAPO,IFRAME,ITRSPT,
     &                IFUSION,IFLOW

* statistics
      COMMON /DTSTA1/ ICREQU,ICSAMP,ICCPRO,ICDPR,ICDTA,
     &                ICRJSS,ICVV2S,ICCHAI(2,9),ICRES(9),ICDIFF(5),
     &                ICEVTG(8,0:30)

      DIMENSION MO(3,2),IDX(3),PCH(4),PP(4),PT(4),P1(4),P2(4)
      DIMENSION IDD(3,2)

      IREJ   = 0

      IDX(1) = IDX1
      IDX(2) = IDX2
      IDX(3) = IDX3
      IF(JMOHKK(1,IDX1).NE.IDX1-2)THEN
        IREJ=1
        RETURN
      ENDIF
      IF(JMOHKK(2,IDX1).NE.IDX1-1)THEN
        IREJ=1
        RETURN
      ENDIF
      IF(JMOHKK(1,IDX2).NE.IDX2-2)THEN
        IREJ=1
        RETURN
      ENDIF
      IF(JMOHKK(2,IDX2).NE.IDX2-1)THEN
        IREJ=1
        RETURN
      ENDIF
      IF(JMOHKK(1,IDX3).NE.IDX3-2)THEN
        IREJ=1
        RETURN
      ENDIF
      IF(JMOHKK(2,IDX3).NE.IDX3-1)THEN
        IREJ=1
        RETURN
      ENDIF
      PPTT1=PHKK(1,IDX1-2)**2+PHKK(2,IDX1-2)**2
      PPTT2=PHKK(1,IDX1-1)**2+PHKK(2,IDX1-1)**2
      PPTT3=PHKK(1,IDX2-2)**2+PHKK(2,IDX2-2)**2
      PPTT4=PHKK(1,IDX2-1)**2+PHKK(2,IDX2-1)**2
      PPTT5=PHKK(1,IDX3-2)**2+PHKK(2,IDX3-2)**2
      PPTT6=PHKK(1,IDX3-1)**2+PHKK(2,IDX3-1)**2
      PPTTMAX=MAX(PPTT1,PPTT2,PPTT3,PPTT4,PPTT5,PPTT6)
C     IF(PPTTMAX.GT.2.D0)THEN
        IF(PPTTMAX.GT.402.D0)THEN
           IREJ=1
           RETURN
C       ENDIF
           PPTTPLUS=PPTTMAX-2.D0
           PTLIN=(PPTTPLUS/400.D0)**0.5D0
           IF(PTLIN.GT.DT_RNDM(VVV))THEN
C        IREJ=1
C        RETURN
        ENDIF
      ENDIF
      DO 1 I=1,3
         DO 2 J=1,2
            MO(I,J) = JMOHKK(J,IDX(I))
            IDD(I,J) = IDHKK(MO(I,J))
    2    CONTINUE
    1 CONTINUE
C     WRITE(6,*)'Join333'
C    * ,IDD(1,1),IDD(1,2),IDD(2,1),IDD(2,2),IDD(3,1),IDD(3,2),'NN',
C    *IDX1,MO(1,1),MO(1,2),IDX2,MO(2,1),MO(2,2),IDX3,MO(3,1),MO(3,2)

* join chains
      DO 3 K=1,4
         PP(K) = PHKK(K,MO(1,1))+PHKK(K,MO(2,1))+PHKK(K,MO(3,1))
         PT(K) = PHKK(K,MO(1,2))+PHKK(K,MO(2,2))+PHKK(K,MO(3,2))
    3 CONTINUE
      IST1 = ISTHKK(MO(3,1))
      IST2 = ISTHKK(MO(3,2))
      IF2=IDD(1,2)
      IDMM1=IDD(3,1)/1000
      IDMM2=(IDD(3,1)-IDMM1*1000)/100
      IF(IDD(1,1).EQ.-IDMM1)THEN
         IF(IDD(2,1).GE.IDMM2)IF1=1000*IDD(2,1)+100*IDMM2+3
         IF(IDMM2.GT.IDD(2,1))IF1=1000*IDMM2+100*IDD(2,1)+3
      ELSEIF(IDD(1,1).EQ.-IDMM2)THEN
         IF(IDD(2,1).GE.IDMM1)IF1=1000*IDD(2,1)+100*IDMM1+3
         IF(IDMM1.GT.IDD(2,1))IF1=1000*IDMM1+100*IDD(2,1)+3
      ENDIF
C     WRITE(6,*)'IF1,IF2 ',IF1,IF2
C     RETURN
* put partons again on mass shell
      XM1 = 0.0D0
      XM2 = 0.0D0
      IF (IMSHL.EQ.1) THEN

         XM1 = PYMASS(IF1)
         XM2 = PYMASS(IF2)

      ENDIF
C     WRITE(6,*)'effect of mashel'
      PPPT=PP(1)**2+PP(2)**2
      PTPT=PT(1)**2+PT(2)**2
C     WRITE(6,*)'PP,PPPT ',PP,PPPT
C     WRITE(6,*)'PT,PTPT ',PT,PTPT
      CALL DT_MASHEL(PP,PT,XM1,XM2,P1,P2,IREJ1)
      IF (IREJ1.NE.0) GOTO 9999
      P1PT=P1(1)**2+P1(2)**2
      P2PT=P2(1)**2+P2(2)**2
      DO 4 I=1,4
         PP(I) = P1(I)
         PT(I) = P2(I)
    4 CONTINUE
C     WRITE(6,*)'P1,P1PT ',P1,P1PT
C     WRITE(6,*)'P2,P2PT ',P2,P2PT
      IF(P1PT.GE.PPPT+0.4D0)GO TO 9999
      IF(P2PT.GE.PTPT+0.4D0)GO TO 9999

* store new partons in DTEVT1
      CALL DT_EVTPUT(IST1,IF1,MO(1,1),MO(2,1),PP(1),PP(2),PP(3),PP(4),
     &                                                       0,0,0)
      CALL DT_EVTPUT(IST2,IF2,MO(1,2),MO(2,2),PT(1),PT(2),PT(3),PT(4),
     &                                                       0,0,0)
      DO 5 K=1,4
         PCH(K) = PP(K)+PT(K)
    5 CONTINUE


      ICCHAI(2,9) = ICCHAI(2,9)+1
* store new chain in DTEVT1
      KCH = 191
      CALL DT_EVTPUT(KCH,66666,-2,-1,PCH(1),PCH(2),PCH(3),PCH(4),0,0,9)
      IDHKK(IDX(1)) = 22222
      IDHKK(IDX(2)) = 22222
      IDHKK(IDX(3)) = 22222
* special treatment for space-time coordinates
      DO 6 K=1,4
         VHKK(K,NHKK) = (VHKK(K,IDX(1))+VHKK(K,IDX(2))+
     *        VHKK(K,IDX(3)))/3.0D0
         WHKK(K,NHKK) = (WHKK(K,IDX(1))+WHKK(K,IDX(2))+
     *        WHKK(K,IDX(3)))/3.0D0
    6 CONTINUE
C     WRITE(6,*)'Join333 chains IDX1,IDX2,IDX3,NHKK',IDX1,IDX2,IDX3,NHKK
      RETURN

 9999 CONTINUE
      IREJ = 1
      RETURN
      END
