C***  plot curve Y2
C
        DO 1250 K=1,N
           L=NINT((X(K)-XMIN)/XZOOM)
           I=NINT((YMAX-Y2(K))/YZOOM)
           COL(L,I)=SYMB(2)
1250    CONTINUE
      ENDIF
C
C***  write it
C
      IF (LPRI.GT.4)
     &WRITE(LOUT,'(1X,79A)') ('-',I=1,IBREIT)
C
C***  write range of X
C
      XZOOM = (XMAX-XMIN)/DBLE(7)
      IF (LPRI.GT.4)
     &WRITE(LOUT,120) (XZOOM*DBLE(I-1)+XMIN,I=1,7)
